boolean validateWrite()
{
    boolean ret = super();

    if (ret)
    {
        if (routeOpr.ItemCode != TableGroupAll::All && routeOpr.ItemRelation== '')
        {
            return checkFailed("@SYS24595");
        }

        if (routeOpr.RouteCode != RouteAll::All  && routeOpr.RouteRelation== '')
        {
            return checkFailed("@SYS24594");
        }
    }

    return ret;
}

void init()
{
    QueryBuildDataSource    qbdsRouteOpr;

    super();

    qbdsRouteOpr = this.query().dataSourceName(routeOpr_ds.name());

    if (element.args().dataset() == tableNum(WrkCtrTable))
    {
        QueryBuildDataSource    qbdsRouteOprActivity;
        QueryBuildDataSource    qbdsWrkCtrActivity;
        WrkCtrTable             wrkCtrTable;

        qbdsRouteOpr.clearDynalinks();
        wrkCtrTable = element.args().record();

        // Limit the query to only those activities where the resource can potentially be used
        qbdsRouteOprActivity = qbdsRouteOpr.addDataSource(tableNum(WrkCtrRouteOprActivity));
        qbdsRouteOprActivity.joinMode(JoinMode::ExistsJoin);
        qbdsRouteOprActivity.addLink(fieldNum(RouteOpr,RecId),fieldNum(WrkCtrRouteOprActivity,RouteOpr));

        qbdsWrkCtrActivity = qbdsRouteOprActivity.addDataSource(tableNum(WrkCtrActivity));
        qbdsWrkCtrActivity.joinMode(JoinMode::ExistsJoin);
        qbdsWrkCtrActivity.addLink(fieldNum(WrkCtrRouteOprActivity,Activity),fieldNum(WrkCtrActivity,RecId));

        filterId = WrkCtrActivityRequirementSet::limitActivityQueryOnResource(qbdsWrkCtrActivity, WrkCtrActivityType::RouteOpr, wrkCtrTable);
    }

    if (linkOpr)
    {
        qbdsRouteOpr.addSortIndex(indexNum(RouteOpr,OprRelIdx));
    }
}

