public boolean modified()
{
    ItemId      oldItemId = routeOpr.ItemRelation;
    boolean     ret = super();

    if (ret)
    {
        if (routeOpr.ItemCode == TableGroupAll::Table)
        {
            if (oldItemId != this.text())
            {
                routeOpr.ConfigId = '';
            }

            identification_ConfigId.enabled(InventTable::find(this.text()).configActive());
        }
    }

    return ret;
}

