public final class RouteTable extends FormRun
{
    NumberSeqFormHandler    numberSeqFormHandler;
    RouteTableForm          routeTableForm;
    FormButtonManager       formButtonManager;
    InventDimCtrl_Frm       inventDimFormSetupRouteVersion;

}

void close()
{
    if (numberSeqFormHandler)
    {
        numberSeqFormHandler.formMethodClose();
    }

    super();
}

public void doRefresh(boolean _executeRouteVersionQuery = false)
{
    routeTable_ds.reread();
    routeTable_ds.refresh();
    routeTable_ds.rereadReferenceDataSources();
    if (_executeRouteVersionQuery)
    {
        routeVersion_ds.executeQuery();
    }
    else
    {
        routeVersion_ds.reread();
        routeVersion_ds.refresh();
        routeVersion_ds.rereadReferenceDataSources();
    }
}

/// <summary>
/// Enable the buttons in the engineering change group, if an engineering change case category exists.
/// </summary>
private void enableEngineeringChangeButtons()
{
    boolean caseCategoryFound = CaseCategoryHierarchyDetail::existsByCategoryType(CaseCategoryType::EngineeringChange);

    engineeringChangeCreateCase.enabled(caseCategoryFound);
    engineeringChangeAssociateCase.enabled(caseCategoryFound);
    engineeringChangeItemAssociateCase.enabled(caseCategoryFound);
    engineeringChangeAddToLog.enabled(caseCategoryFound);
    engineeringChangeItemAddToLog.enabled(caseCategoryFound);
}

void enableFields(InventTable _inventTable)
{
    // Site cannot be changed after it is set
    if (inventDimRouteVersion.InventSiteId)
    {
        inventDimRouteVersion_ds.object(fieldNum(InventDim,InventSiteId)).enabled(false);
    }
    else
    {
        inventDimRouteVersion_ds.object(fieldNum(InventDim,InventSiteId)).enabled(true);
    }
}

void init()
{
    routeTableForm = RouteTableForm::construct();

    super();

    if (RouteParameters::find().MandatoryDate)
    {
        routeVersion_FromDate.mandatory(true);
        routeVersion_ToDate.mandatory(true);
    }

    this.enableEngineeringChangeButtons();

    element.updateDesign(InventDimFormDesignUpdate::Init);

    // setup the formButtonManager Buttons
    formButtonManager = FormButtonManager::construct();
    formButtonManager.parmRuleImplementer(routeTableForm);
    formButtonManager.setDatasources(routeTable, routeVersion);
    formButtonManager.registerButtonRule(groupApprove, ctrlRouteVersionApprove, methodStr(RouteTableForm, checkVersionApprove));

    if (!element.args() || element.args().openMode() != OpenMode::New)
    {
        navigationList.visible(!element.args() || !element.args().record());
    }
}

NumberSeqFormHandler numberSeqFormHandler()
{
    if (!numberSeqFormHandler)
        numberSeqFormHandler = NumberSeqFormHandler::newForm(RouteParameters::numRefRouteId().NumberSequenceId,
                                                             element,
                                                             routeTable_DS,
                                                             fieldNum(RouteTable,RouteId)
                                                            );
    return numberSeqFormHandler;
}

RouteId routeId()
{
    return routeTable.RouteId;
}

void updateDesign(
    InventDimFormDesignUpdate   _mode,
    InventTable                 _inventTable = null)
{
    InventDimParm   inventDimParm;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init        :

            this.inventDimSetupObject();

            inventDimParm.initDimBOMRouteVersion();
            inventDimFormSetupRouteVersion.parmDimParmVisibleGrid(inventDimParm);
            inventDimFormSetupRouteVersion.formSetControls(true);

        case InventDimFormDesignUpdate::Active,
             InventDimFormDesignUpdate::LinkActive,
             InventDimFormDesignUpdate::FieldChange :

            if (_inventTable)
            {
                inventDimParm.initDimBOMRouteVersion(InventDimGroupSetup::newInventTable(_inventTable));
                inventDimFormSetupRouteVersion.parmDimParmEnabled(inventDimParm);
                inventDimFormSetupRouteVersion.formSetControls(true);
            }
            break;

        default :
            throw error(strFmt("@SYS54195",funcName()));
    }
}

InventDimCtrl_Frm inventDimSetupObject()
{
    if (!inventDimFormSetupRouteVersion)
    {
        inventDimFormSetupRouteVersion = InventDimCtrl::newFromForm(element);     
    }

    return inventDimFormSetupRouteVersion;
}

