void init()
{
    QueryBuildDataSource qbds;

    super();

    qbds = this.query().dataSourceNo(1);
    qbds.addSortField(fieldNum(RouteVersion,ItemId));
    qbds.addSortField(fieldNum(RouteVersion,Active));
    qbds.addSortField(fieldNum(RouteVersion,FromDate));
}

public void write()
{
    InventDim   inventDim;

    ttsbegin;

    inventDim = InventDim::findOrCreate(inventDimRouteVersion);
    routeVersion.InventDimId = inventDim.InventDimId;

    super();

    if (routeVersion.InventDimId != inventDimRouteVersion.InventDimId)
    {
        inventDimRouteVersion.data(inventDim);
        inventDimRouteVersion_ds.setCurrent();
    }

    ttscommit;
}

int active()
{
    int             ret;
    InventTable     inventTable;

    ret = super();

    inventTable = routeVersion.inventTable();

    // refresh the version buttons group
    formButtonManager.refreshGroup(groupApprove);
    element.enableFields(inventTable);
    element.updateDesign(InventDimFormDesignUpdate::Active,inventTable);

    return ret;
}

public boolean validateWrite()
{
    boolean ret;
    routeVersion.InventDimId = InventDim::findOrCreate(inventDimRouteVersion).InventDimId;

    ret = super();

    return ret;
}

public void lookup(FormControl _formControl, str _filterStr)
{
    InventLookupItemIdByDefaultOrder inventLookupItemIdByDefaultOrder = InventLookupItemIdByDefaultOrder::newLookupCtrl(_formControl, true);
    inventLookupItemIdByDefaultOrder.parmItemGroupId(routeTable.ItemGroupId);
    inventLookupItemIdByDefaultOrder.parmShowRoute(true);
    inventLookupItemIdByDefaultOrder.run();
}

void modified()
{
    if (! routeTable.RecId)
        routeTable.write();

    super();

    formButtonManager.refreshGroup(groupApprove);
    element.updateDesign(InventDimFormDesignUpdate::FieldChange,InventTable::find(routeVersion.ItemId));
}

