void clicked()
{
    List            selectedRecords = new List(Types::Record);
    RouteVersion    routeVersionSelected;
    InventTable     inventTable;

    // Get the InventTable records for each selected BOMVersion
    routeVersionSelected = routeVersion_ds.getFirst(true);
    if (!routeVersionSelected)
    {
        routeVersionSelected = routeVersion_ds.cursor();
    }

    while (routeVersionSelected)
    {
        inventTable = routeVersionSelected.inventTable();
        selectedRecords.addEnd(inventTable);
        routeVersionSelected = routeVersion_ds.getNext();
    }

    CaseDetailForm::runEngChgCaseAssociateForm(selectedRecords, inventTable);
}

void clicked()
{
    CaseDetailForm::runEngChgCaseAddToLogForm(routeVersion.inventTable());
}
