[Form]
public class RouteVersionFeasibility extends FormRun
{
    InventDimCtrl_Frm                   inventDimFormSetupRouteVersion;
    RouteVersionApplicableResourceCache applicableResourceCache;
    QueryBuildRange                     rangeActivityRequirementSetRecId;
    int                                 routeDSPosition;

}

public void init()
{
    applicableResourceCache = RouteVersionApplicableResourceCache::construct();

    super();

    if (element.args().dataset() != tableNum(RouteVersion))
    {
        throw error(strFmt("@SYS22678",element.name()));
    }

    windowRouteApplicableStatus.imageList(applicableResourceCache.imageList());

    element.updateDesign();
}

public void run()
{
    super();

    // Set focus on the Route grid for fast navigation
    ctrlRoute_OprNum.setFocus();
}

public edit SchedMethod schedMethod(boolean _set, SchedMethod _schedMethod)
{
    if (_set && _schedMethod != applicableResourceCache.parmSchedMethod())
    {
        applicableResourceCache.parmSchedMethod(_schedMethod);
        routeVersion_ds.executeQuery();
    }

    return applicableResourceCache.parmSchedMethod();
}

public edit TransDate searchDate(boolean _set, TransDate _searchDate)
{
    if (_set && _searchDate != applicableResourceCache.parmSearchDate())
    {
        routeDSPosition = route_ds.getPosition();

        applicableResourceCache.parmSearchDate(_searchDate);
        routeVersion_ds.executeQuery();
    }

    return applicableResourceCache.parmSearchDate();
}

void updateDesign()
{
    InventDimParm   inventDimParm;

    if (! inventDimFormSetupRouteVersion)
    {
        inventDimFormSetupRouteVersion = InventDimCtrl::newFromForm(element);
    }

    inventDimParm.initDimBOMRouteVersion();
    inventDimFormSetupRouteVersion.parmDimParmVisibleGrid(inventDimParm);
    inventDimFormSetupRouteVersion.formSetControls(true);
}

public void executeQuery()
{
    // Make sure that the applicable resources are re-read
    applicableResourceCache.reset();

    super();
}

public void init()
{
    RouteVersion            argsRouteVersion;
    QueryBuildDataSource    qbdsRouteVersion;

    super();

    if (element.args().dataset() == tableNum(RouteVersion))
    {
        // Link route version data source to the args
        argsRouteVersion = element.args().record();

        qbdsRouteVersion = this.query().dataSourceName(routeVersion_ds.name());
        qbdsRouteVersion.addDynalink(fieldNum(RouteVersion, RecId), argsRouteVersion, fieldNum(RouteVersion, RecId));

        applicableResourceCache.initFromRouteVersion(argsRouteVersion);
    }
}

public void executeQuery()
{
    super();

    if (routeDSPosition)
    {
        // If only the date was changed then the cursor position must be maintained
        route_ds.setPosition(routeDSPosition);
        routeDSPosition = 0;
    }
}

public int active()
{
    int ret;

    ret = super();

    rangeActivityRequirementSetRecId.value(queryValue(applicableResourceCache.activityRequirementSet(route).RecId));

    wrkCtrActivityRequirementSet_ds.executeQuery();
    wrkCtrTableApplicable_ds.executeQuery();

    return ret;
}

