public void init()
{
    QueryBuildDataSource qbdsRoute;

    super();

    qbdsRoute = route_ds.query().dataSourceName(route_ds.name());
    qbdsRoute.clearDynalinks();
    qbdsRoute.addDynalink(fieldNum(Route,RouteId),routeVersion,fieldNum(RouteVersion, RouteId));
}

