public void jumpRef()
{
    WrkCtrTable::jumpRefWrkCtrId(WrkCtrResourceAndGroupView.WrkCtrIdGroup);
}

public void init()
{
    super();

    rangeActivityRequirementSetRecId = this.query().dataSourceName(wrkCtrActivityRequirementSet_ds.name()).addRange(fieldNum(WrkCtrActivityRequirementSet, RecId));
    rangeActivityRequirementSetRecId.value(queryValue(0));
}

public Common activeSubtype()
{
    return wrkCtrActivityRequirement.determineSubtype( wrkCtrActivityCapabilityRequirement,
                                                wrkCtrActivityCertificateRequirement,
                                                wrkCtrActivityCourseRequirement,
                                                wrkCtrActivityResourceRequirement,
                                                wrkCtrActivityResourceGroupRequirement,
                                                wrkCtrActivitySkillRequirement,
                                                wrkCtrActivityResourceTypeRequirement,
                                                wrkCtrActivityPersonTitleRequirement);
}

public void init()
{
    super();

    this.cacheAddMethod(tableMethodStr(WrkCtrActivityRequirement, displayRequirement));
    this.cacheAddMethod(tableMethodStr(WrkCtrActivityRequirement, displayRequirementDescription));
}

public void executeQuery()
{
    Query                   query;
    QueryBuildDataSource    qbdsWrkCtrResourceAndGroupView;

    if (route)
    {
        query = new Query(applicableResourceCache.applicableResourceQuery(route));

        qbdsWrkCtrResourceAndGroupView = query.dataSourceTable(tableNum(WrkCtrTable)).addDataSource(tableNum(WrkCtrResourceAndGroupView));
        qbdsWrkCtrResourceAndGroupView.addLink(fieldNum(WrkCtrTable,WrkCtrId),fieldNum(WrkCtrResourceAndGroupView,WrkCtrId));

        qbdsWrkCtrResourceAndGroupView.addRange(fieldNum(WrkCtrResourceAndGroupView,ValidFrom)).value(SysQuery::range(null, applicableResourceCache.parmSearchDate()));
        qbdsWrkCtrResourceAndGroupView.addRange(fieldNum(WrkCtrResourceAndGroupView,ValidTo)).value(SysQuery::range(applicableResourceCache.parmSearchDate(), null));

        this.query(query);

        super();
    }
}

void clicked()
{
    super();

    element.searchDate(true, applicableResourceCache.parmSearchDate() - 1);
}

void clicked()
{
    super();

    element.searchDate(true, applicableResourceCache.parmSearchDate() + 1);
}

