    private Query createQueryForOtherMembers()
{
    Query                   query;
    QueryBuildDataSource    qDS_PayTaxDeductionTable, qDS_PayFundMember;

    query = new Query();

    qDS_PayTaxDeductionTable = query.addDataSource(tablenum(RpayTaxDeductionTable));
    qDS_PayTaxDeductionTable.addSelectionField(fieldnum(RpayTaxDeductionTable,RpayTaxDeductionCode));
    qDS_PayTaxDeductionTable.addSelectionField(fieldnum(RpayTaxDeductionTable, Name));
    qDS_PayTaxDeductionTable.addRange(fieldnum(RpayTaxDeductionTable,RpayTaxDeductionType));
    qDS_PayFundMember = qDS_PayTaxDeductionTable.addDataSource(tablenum(RPayFundTaxDeductionMember));
    qDS_PayFundMember.addRange(fieldnum(RPayFundTaxDeductionMember,RpayFundDeductionCode));
    qDS_PayFundMember.joinMode(JoinMode::NoExistsJoin);
    qDS_PayFundMember.addLink(fieldnum(RpayTaxDeductionTable,RpayTaxDeductionCode), fieldnum(RPayFundTaxDeductionMember,RpayTaxDeductionCode));

    return query;
}

    private Query createQueryForOtherMembersCtype()
{
    Query                   query;
    QueryBuildDataSource    qDS_PayTable, qDS_PayFundCtypeMember;

    query = new Query();

    qDS_PayTable = query.addDataSource(tablenum(RPayTable));
    qDS_PayTable.addSelectionField(fieldnum(RPayTable,PayCtype));
    qDS_PayTable.addSelectionField(fieldnum(RPayTable, Name));
    qDS_PayTable.addRange(fieldnum(RPayTable,PayChargeType));
    qDS_PayFundCtypeMember = qDS_PayTable.addDataSource(tablenum(RPayFundCtypeDeductionMember));
