    void initFundDeductionMembers()
{
    m_qListSelectMembers = this.createQueryForSelectMembers();
    m_qListOtherMembers  = this.createQueryForOtherMembers();

    m_imageList = new ImageListAppl_PayGroup_RU();

    m_listSelectMembers = ListSelectMembers;
    m_listSelectMembers.addColumn(1,new FormListColumn("@GLS105850",1));
    m_listSelectMembers.addColumn(2,new FormListColumn("@GLS105748",2, 250));


    m_listSelectMembers.width(280);

    m_listOtherMembers = ListOtherMembers;
    m_listOtherMembers.addColumn(1,new FormListColumn("@GLS105850",1));
    m_listOtherMembers.addColumn(2,new FormListColumn("@GLS105748",2, 250));
    m_listOtherMembers.width(280);
}

