    private void initListOtherMembers()
{
    int                   iIdx;
    QueryRun              qr;
    RpayTaxDeductionTable payTaxDeductionTable;
    FormListItem          formListItem;
    #resappl

    this.setWhereForListOtherMembers(rpayFundDeductionTable.RpayFundDeductionCode);
    qr = new QueryRun(m_qListOtherMembers);
    m_listOtherMembers.lockWindowUpdate(true);
    m_listOtherMembers.deleteAll();

    while (qr.next())
    {
        payTaxDeductionTable = qr.get(tablenum(RpayTaxDeductionTable));
        formListItem = new FormListItem(int2str(payTaxDeductionTable.RpayTaxDeductionCode),m_imageList.image(#ImageReportSum));
        formListItem.data(payTaxDeductionTable.RpayTaxDeductionCode);
        iIdx = m_listOtherMembers.addItem(formListItem);
        formListItem = new FormListItem(payTaxDeductionTable.Name);
        formListItem.subItem(1);
        formListItem.idx(iIdx);
        m_listOtherMembers.setItem(formListItem);
    }

    m_listOtherMembers.lockWindowUpdate(false);
}

