    void membersInOtherList(FormListNext _formListNext)
{
    FormListItem               formListItem;
    int                        iIndx;
    RpayTaxDeductionCode       taxDeductionCode;

    try
    {
        ttsbegin;

        iIndx = m_listSelectMembers.getNextItem(_formListNext);

        while (iIndx != -1)
        {
            formListItem = m_listSelectMembers.getItem(iIndx);

            taxDeductionCode = formListItem.data();

            RPayFundTaxDeductionMember::deleteMember(rpayFundDeductionTable.RpayFundDeductionCode, taxDeductionCode);

            iIndx = m_listSelectMembers.getNextItem(_formListNext,iIndx);
        }

        ttscommit;

        element.initListOtherMembers();
        element.initListSelectMembers();
    }
    catch(Exception::Error)
    {
        ttsabort;
    }
    catch(Exception::Warning)
    {
        ttsabort;
    }
}

