    private void setWhereForListOtherMembers(RpayFundDeductionCode _fundDeductionCode)
{
    QueryBuildDataSource    qDS;
    QueryBuildRange         qRange;

    qDS    = m_qListOtherMembers.dataSourceTable(tablenum(RPayFundTaxDeductionMember));
    qRange = qDS.rangeField(fieldnum(RPayFundTaxDeductionMember, RpayFundDeductionCode));
    qRange.value(queryValue(_fundDeductionCode));

    qDS    = m_qListOtherMembers.dataSourceTable(tablenum(RpayTaxDeductionTable));
    qRange = qDS.rangeField(fieldnum(RpayTaxDeductionTable, RpayTaxDeductionType));
    qRange.value(queryValue(RpayTaxDeductionType::Other));
}

