    void updateDesign()
{
    switch (rPayFundDeductionTable.RpayFundDeductionType)
    {
        case RpayFundDeductionType::FundDeduction:
            taxDeduction.visible(false);
            costType.visible(false);
            break;
        case RpayFundDeductionType::TaxDeduction:
            taxDeduction.visible(true);
            costType.visible(false);
            break;
        case RpayFundDeductionType::CtypeDeduction:
            taxDeduction.visible(false);
            costType.visible(true);
            break;
    }

    rpayFundDeductionTable_ds.object(fieldnum(RpayFundDeductionTable, ActOnRelative))
        .allowEdit(rPayFundDeductionTable.RpayFundDeductionType == RpayFundDeductionType::CtypeDeduction ||
                   rPayFundDeductionTable.RpayFundDeductionType == RpayFundDeductionType::TaxDeduction);
}

