Method.Source:
    [Form]
    public class RpayHrmOrganization extends FormRun
    {
        RHRMOrganizationTree         rhrmOrganizationTree;
        boolean                      lookupMode;
        boolean                      showOnlyActiveLast;
        boolean                      showTreeLast;
        str                          lookupValue;

        LogisticsPostalAddressFormHandler addressController;

        #DEFINE.CurrentVersion(2)

        #LOCALMACRO.CurrentList
            showOnlyActiveLast,
            showTreeLast
        #ENDMACRO
}

void close()
{
    if (lookupMode)
    {
        element.saveSettings();
    }

    super();

    if (rhrmOrganizationTree)
    {
        rhrmOrganizationTree.finalize();
    }
}

void init()
{
    super();

    addressController = LogisticsPostalAddressFormHandler::newParameters(rPayHRMOrganization_DS, logisticsPostalAddress_ds);
    addressController.callerUpdateQuery(fieldNum(RPayHRMOrganization, LogisticsLocation));

    lookupMode = ! (element.args().record());

    rhrmOrganizationTree = new RHRMOrganizationTree(element.dataSource(),
                                                    tree,
                                                    fieldNum(RPayHRMOrganization, HrmOrganizationId),
                                                    fieldNum(RPayHRMOrganization, ParentOrganizationId),
                                                    fieldNum(RPayHRMOrganization, Description));

    if (lookupMode)
    {
        lookupValue = element.args().lookupValue();

        element.initiateSettings();
        rhrmOrganizationTree.organizationType(showOnlyActive.value() ? true : false);
        rhrmOrganizationTree.initRoot("@GLS106284", '');
        rhrmOrganizationTree.hideShowTree(showTree.value(), overview, tab, true);
    }
    else
    {
        rhrmOrganizationTree.hideShowTree(false,overview,tab,true);
        showOnlyActive.enabled(false);
        showTree.enabled(false);
        typeGroup.enabled(false);
    }

    if (! showTree.value())
    {
        rPayHRMOrganization_ds.autoSearch(true);
    }
}

void run()
{
    super();

    if (lookupMode)
    {
        RpayHRMOrganization_ds.positionToRecord(RpayHRMOrganization::find(lookupValue));
        RpayHRMOrganization_ds.refresh();

        if (showTree.value())
        {
            rhrmOrganizationTree.hideShowTree(showTree.value(), overview, tabGrid);
        }
    }
}

void saveSettings()
{
    showOnlyActiveLast  = showOnlyActive.value();
    showTreeLast        = showTree.value();

    xSysLastValue::saveLast(this);
}

public boolean unpack(container _packedClass)
{
    Integer version     = conPeek(_packedClass,1);
    switch (version)
    {
        case #CurrentVersion :
            [version, #CurrentList] = _packedClass;
            break;
        default :
            return false;
    }

    return true;
}

int active()
{
    int     ret;

    ret = super();

    if (ret)
    {
        addressController.callerActive();
        addressController.callerUpdateButtons(newAddress, editAddress, clearAddress, mapButton);
        rPayHRMOrganization_ds.allowCreate(! (showTree.value() && tree.getRoot() != tree.getSelection()));
    }

    return ret;
}

