#WinAPI
void navigate()
{
    FilenameOpen selectedFile;
    container conFilter = ['Файлы XML (*.xml)', '*.xml'];

    if (rPayTaxListTable.FileName && WinAPI::fileExists_RU(rPayTaxListTable.FileName))
    {
        if (WinAPI::getFileAttributes(rPayTaxListTable.FileName) & #FILE_ATTRIBUTE_DIRECTORY)
        {
            selectedFile = WinAPI::getOpenFileName( element.hWnd(), conFilter, rPayTaxListTable.FileName, '');
            if (selectedFile)
            {
                webBrowser.navigate(selectedFile);
            }
        }
        else
        {
            webBrowser.navigate(element.getFileName());
        }
    }
    else
    {
        webBrowser.navigate('about:blank');
    }
}

void updateDesign()
{
    boolean allowEditTaxAuthority,
            allowEditEventualTaxAuthority;

    if (RPayTaxParameters::find().CalcByBranch)
    {
        rpayTaxListTable_ds.object(fieldNum(RPayTaxListTable, TaxAuthority)).visible(true);
        rpayTaxListTable_ds.object(fieldNum(RPayTaxListTable, EventualTaxAuthority)).visible(true);
        rpayTaxListTable_ds.object(fieldNum(RPayTaxListTable, Presentation)).visible(true);

        switch (rpayTaxListTable.Presentation)
        {
            case RPayTaxPresentation::ToPrimaryIFNS:
                allowEditTaxAuthority           = false;
                allowEditEventualTaxAuthority   = false;
                break;

            case RPayTaxPresentation::ThroughPrimaryIFNS:
                allowEditTaxAuthority           = false;
                allowEditEventualTaxAuthority   = true;
                break;

            case RPayTaxPresentation::ToSeparateDevisionIFNS:
                allowEditTaxAuthority           = true;
                allowEditEventualTaxAuthority   = false;
                break;
        }

        rpayTaxListTable_ds.object(fieldNum(RPayTaxListTable, TaxAuthority)).allowEdit(allowEditTaxAuthority);
        rpayTaxListTable_ds.object(fieldNum(RPayTaxListTable, EventualTaxAuthority)).allowEdit(allowEditEventualTaxAuthority);
    }
    else
    {
        rpayTaxListTable_ds.object(fieldNum(RPayTaxListTable, Presentation)).visible(false);
        rpayTaxListTable_ds.object(fieldNum(RPayTaxListTable, TaxAuthority)).visible(false);
        rpayTaxListTable_ds.object(fieldNum(RPayTaxListTable, EventualTaxAuthority)).visible(false);
    }

    rPayTaxListCrateAuto.visible(RPayTaxParameters::find().CalcByBranch);

    rpayTaxListTable_ds.refresh();
}

