public int active()
{
    boolean readAmount;
    int ret;

    ret = super();

    if (ret)
    {
        if (rPayTaxListTrans.RecId)
        {
            rPayTaxListTrans_ds.allowEdit(rPayTaxListTrans.TaxRateCode ? true : false);
        }

        readAmount = !(rPayTaxListTrans.ReportSign == 2 && rpayTaxListTable.ReportType == RPayNDFLType::EmplDebt);

        rPayTaxListTrans_ds.object(fieldNum(RPayTaxListTrans, IncomeSum)).allowEdit(!readAmount);
        rPayTaxListTrans_ds.object(fieldNum(RPayTaxListTrans, AddTaxSum)).allowEdit(readAmount);
        rPayTaxListTrans_ds.object(fieldNum(RPayTaxListTrans, BudgetTransferedTaxAmount)).allowEdit(readAmount);
    }

    return ret;
}

