public void lookup(FormControl _formControl, str _filterStr)
{
    SysTableLookup          sysTableLookup;
    Query                   query = new Query();
    QueryBuildDataSource    qbds;
    QueryBuildRange         qbr;
    Range                   range;
    Set                     rateCodeSet = new Set(Types::String);
    SetEnumerator           rateCodeSetEnumerator;
    RPayTaxIncomeGroupTable rPayTaxIncomeGroupTable;
    ;

    sysTableLookup = SysTableLookup::newParameters(tableNum(RPayRateTable), _formControl);

    sysTableLookup.addLookupfield(fieldNum(RPayRateTable, PayRate));
    sysTableLookup.addLookupfield(fieldNum(RPayRateTable, Name));

    qbds = query.addDataSource(tableNum(RPayRateTable));
    qbds.clearRanges();

    while select RateCode, RateCodeGroup from rPayTaxIncomeGroupTable
    {
        if (rPayTaxIncomeGroupTable.RateCode)
        {
            rateCodeSet.add(rPayTaxIncomeGroupTable.RateCode);
        }

        if (rPayTaxIncomeGroupTable.RateCodeGroup)
        {
            rateCodeSet.add(rPayTaxIncomeGroupTable.RateCodeGroup);
        }
    }

    rateCodeSetEnumerator = rateCodeSet.getEnumerator();

    while (rateCodeSetEnumerator.moveNext())
    {
        range += ',' + rateCodeSetEnumerator.current();
    }

    range = strDel(range, 1, 1);

    if (range)
    {
        qbr = qbds.addRange(fieldNum(RPayRateTable, PayRate));
        qbr.value(range);
        qbr.status(RangeStatus::Hidden);
    }

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

