public void executeQuery()
{
    select officialsTrans_RU
        where officialsTrans_RU.ReportType   == OffReportType_RU::RPayTaxList &&
              officialsTrans_RU.OffSessionId == rPayTaxListTable.OffSessionId;
    super();
}

