public class SMAAgreementTable extends FormRun implements OfficeIGeneratedWorkbookCustomExporter, OfficeIMenuCustomizer
{
    private const str ExportServiceAgreementHeaderAndLinesToExcelOptionId = 'ServiceAgreementHeaderAndLinesCustom';

    QueryBuildRange                 qbrTemplateFilter;
    NumberSeqFormHandler            numberSeqFormHandler;
    InventDimCtrl_Frm_ServiceMgmt   inventDimFormSetup;

    // Local Cache variables
    [FormObservable]
    ProjCostPriceCost               costPrice;
    [FormObservable]
    ProjSalesPriceCost              salesPrice;

