public void close()
{
    FormRun    callerForm;

    if (numberSeqFormHandler)
    {
        numberSeqFormHandler.formMethodClose();
    }

    super();
}

void getPrices()
{
    costPrice = 0;
    salesPrice = 0;

    // Is the transaction a hour, expense or item transaction?
    if (smaAgreementLine.TransactionType == SMATransactionType::Hour || smaAgreementLine.TransactionType == SMATransactionType::Expense || smaAgreementLine.TransactionType == SMATransactionType::Item)
    {
        // Calculate cost price
        costPrice = smaAgreementLine.SMAServiceLineMap::projCostPrice();
        smaAgreementLine_ds.lineProjCostAmount();
    }

    // Calculate sales price
    salesPrice = smaAgreementLine.SMAServiceLineMap::projSalesPrice(costPrice);
}

public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    List exportOptions = _menuOptions.customMenuItems();

    var exportOption = OfficeGeneratedExportMenuItem::construct(tableStr(SMAServiceAgreementLineEntity), ExportServiceAgreementHeaderAndLinesToExcelOptionId);
    exportOption.displayName(strfmt("@SYS76681", "@ServiceManagement:SMAServiceAgreementHeaderAndLines", '', curExt()));
    exportOptions.addEnd(exportOption);
}

public ExportToExcelDataEntityContext getDataEntityContext(OfficeGeneratedExportMenuItem _menuItem)
{
    ExportToExcelDataEntityContext context = null;

    switch (_menuItem.id())
    {
        case ExportServiceAgreementHeaderAndLinesToExcelOptionId:
            context = ExportToExcelDataEntityContext::constructDefault(dataentityviewstr(SMAServiceAgreementHeaderEntity));

            var serviceAgreementHeaderEntityInfo = context.findEntityByName(dataentityviewstr(SMAServiceAgreementHeaderEntity));

            if (serviceAgreementHeaderEntityInfo)
            {
                var serviceAgreementHeaderFilterBuilder = new ExportToExcelFilterTreeBuilder(dataentityviewstr(SMAServiceAgreementHeaderEntity));
                var serviceAgreementHeaderIdFilter = serviceAgreementHeaderFilterBuilder.areEqual(fieldStr(SMAServiceAgreementHeaderEntity, ServiceAgreementId), SMAAgreementTable.AgreementId);

                serviceAgreementHeaderEntityInfo.filter(serviceAgreementHeaderFilterBuilder.and(serviceAgreementHeaderFilterBuilder.companyFilter(), serviceAgreementHeaderIdFilter));
            }

            var serviceAgreementLineEntityInfo = context.addEntityDefault(dataentityviewstr(SMAServiceAgreementLineEntity));
            var serviceAgreementLineFilterBuilder = new ExportToExcelFilterTreeBuilder(dataentityviewstr(SMAServiceAgreementLineEntity));
            var serviceAgreementLineIdFilter = serviceAgreementLineFilterBuilder.areEqual(fieldStr(SMAServiceAgreementLineEntity, ServiceAgreementId), SMAAgreementTable.AgreementId);
            serviceAgreementLineEntityInfo.filter(serviceAgreementLineFilterBuilder.and(serviceAgreementLineFilterBuilder.companyFilter(), serviceAgreementLineIdFilter));
            break;
    }

    return context;
}

