void setFieldAccess(SMATransactionType _transactionType)
{
    boolean     showItem    = false;
    boolean     showHour    = false;
    boolean     showFee     = false;
    boolean     showExpense = false;

    switch (_transactionType)
    {
        case SMATransactionType::Hour:
            showHour = true;
            break;

        case SMATransactionType::Expense:
            showExpense = true;
            break;

        case SMATransactionType::Fee:
            showFee = true;
            break;

        case SMATransactionType::Item:
            showItem = true;
            break;
    }

    // Line fields
    boolean showActivityIdFeatureEnabled = FeatureStateProvider::isFeatureEnabled(SMAAgreementAndServiceOrderItemTypeShowActivityIdFeature::instance());
    smaAgreementLine_ds.object(fieldNum(SMAAgreementLine, ActivityNumber)).visible(showHour || (showItem && showActivityIdFeatureEnabled));
    smaAgreementLine_ds.object(fieldNum(SMAAgreementLine, ActivityNumber)).allowEdit(showHour || (showItem && showActivityIdFeatureEnabled));

    smaAgreementLine_ds.object(fieldNum(SMAAgreementLine, Qty)).visible(showHour || showItem || showExpense);
    smaAgreementLine_ds.object(fieldNum(SMAAgreementLine, Qty)).allowEdit(showHour || showItem || showExpense);

    // Item fields are visible/enabled only on Item trans type
    lineProjIdentification_ItemId.visible(showItem);
    smaAgreementLine_ds.object(fieldNum(SMAAgreementLine, ItemId)).allowEdit(!smaAgreementLine.RecId && showItem);

    smaAgreementLine_ds.object(fieldNum(SMAAgreementLine, Unit)).visible(showItem);

    smaAgreementLine_ds.object(fieldNum(SMAAgreementLine, InventDimId)).visible(showItem);
    smaAgreementLine_ds.object(fieldNum(SMAAgreementLine, InventDimId)).allowEdit(showItem);

    lineProjIdentification_ItemSetupType.visible(showItem);
    smaAgreementLine_ds.object(fieldNum(SMAAgreementLine, ItemSetupType)).allowEdit(!smaAgreementLine.RecId && showItem);

    // Controls
    lineProjCostPrice_M_ProjCostPrice.visible(showExpense || showHour || showItem);

    lineProjCostPrice.visible(!showFee);

    // Groups
    lineServiceTime.visible(showHour);
    storageDimensions.visible(showItem);
    productDimensions.visible(showItem);
    trackingDimensions.visible(showItem);

    if (smaAgreementLine.TransactionType == SMATransactionType::Item)
    {
        element.updateDesign(InventDimFormDesignUpdate::FieldChange);
    }

    // Buttons enable/disable
    ctrlLineInventOnHandLine.enabled(showItem);

    // Change label
    if (showHour)
    {
        // Hours
        lineProjCostPrice_Qty.label("@SYS1151");
    }
    else
    {
        // Quantity
        lineProjCostPrice_Qty.label(smaAgreementLine_ds.object(fieldNum(SMAAgreementLine, Qty)).labelText());
    }

    tabLineDimension.visible(showItem);
}

