public int active()
{
    int ret;

    ret = super();

    // If lines exist no change to start date can be done
    smaAgreementTable_ds.object(fieldNum(SMAAgreementTable, StartDate)).enabled(!SMAAgreementLine::existAgreementLine(smaAgreementTable.AgreementId));

    // It is not possible to convert to template if service order lines have been created for this agreement
    smaAgreementTable_ds.object(fieldNum(SMAAgreementTable, TemplateGroupId)).enabled(!SMAServiceOrderLine::existAgreementId(smaAgreementTable.AgreementId));

    element.setButtonAccess();

    smaAgreementTable_ds.object(fieldNum(SMAAgreementTable, ServiceEventGroup_IN)).enabled(SMAAgreementTable.ContinuousService_IN);

    return ret;
}

