public void linkActive()
{
    // Is form called with a record
    if (element.args() && element.args().record())
    {
        // Is the form called from a customer
        if (element.args().dataset() == tableNum(CustTable))
        {
            CustTable custTable = element.args().record();
            CustAccount custAccount = custTable.AccountNum;

            // Get the manual created ProjTable datasource
            QueryBuildDataSource queryBuildDataSource = this.query().dataSourceTable(tableNum(ProjTable));

            QueryBuildRange rangeCustAccount;

            // If the datasource isn't created already
            if (!queryBuildDataSource)
            {
                // Add the extra ProjTable data source
                queryBuildDataSource    = this.query().dataSourceNo(1).addDataSource(tableNum(ProjTable));

                if (!SMAAgreementProjectCustAccountRangeFlight::instance().isEnabled())
                {
                    // Add a new range for filtering on customer account
                    rangeCustAccount   = queryBuildDataSource.addRange(fieldNum(ProjTable, CustAccount));
                }

                // Link the new datasource to the existing query
                queryBuildDataSource.joinMode(JoinMode::ExistsJoin);
                queryBuildDataSource.fetchMode(QueryFetchMode::One2One);
                queryBuildDataSource.addLink(fieldNum(SMAAgreementTable, ProjId), fieldNum(ProjTable, ProjId));

                if (!SMAAgreementProjectCustAccountRangeFlight::instance().isEnabled())
                {
                    // The new datasource should be hidden in the query selection form
                    rangeCustAccount.status(RangeStatus::Hidden);
                }
            }

            if (SMAAgreementProjectCustAccountRangeFlight::instance().isEnabled())
            {
                // Add a new range for filtering on customer account
                rangeCustAccount = queryBuildDataSource.addRange(fieldNum(ProjTable, CustAccount));

                // The new datasource should be hidden in the query selection form
                rangeCustAccount.status(RangeStatus::Hidden);
            }

            // Filter on the customer
            rangeCustAccount.value(queryValue(custAccount));
        }
    }
    element.numberSeqFormHandler().formMethodDataSourceLinkActive();

    super();
}

public boolean validateDelete()
{
    boolean ret;

    ret = super();

    if (ret && SMAAgreementLine::existAgreementLine((smaAgreementTable.AgreementId)))
    {
        // Delete service agreement lines?
        if (Box::yesNo("@SYS91003", DialogButton::No) == DialogButton::No)
        {
            // Deletion has been canceled
            ret = checkFailed("@SYS19437");
        }
    }

    return ret;
}

