display ProjSalesPriceCost showProjSalesPrice()
{
    salesPrice = smaAgreementLine.SMAServiceLineMap::projSalesPrice(costPrice);

    return salesPrice;
}

display ProjCostPriceCost showProjCostPrice()
{
    if (smaAgreementLine.TransactionType == SMATransactionType::Hour && (!smaAgreementLine.Worker || !smaAgreementLine.ProjCategoryId))
    {
        costPrice = 0.0;
    }
    else
    {
        costPrice = smaAgreementLine.SMAServiceLineMap::projCostPrice();
    }

    smaAgreementLine_ds.lineProjCostAmount();

    return costPrice;
}

