public void write()
{
    if (smaAgreementLine.TransactionType == SMATransactionType::Item)
    {
        smaAgreementLine.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;
    }
    else
    {
        smaAgreementLine.InventDimId = InventDim::inventDimIdBlank();
    }

    if (smaAgreementLine.AgreementLineNum == 0)
    {
        smaAgreementLine.newAgreementLineNum();
    }

    super();

    element.setButtonAccess();
}

