void doLinkActive(Common _caller)
{
    SMAServiceObjectRelation serviceObjectRelation;

    if (!_caller.RecId)
    {
        return;
    }

    try
    {
        switch (_caller.TableId)
        {
            case (tableNum(SMATemplateBOMTable)) :
                curInventTable = smaTemplateBOMTable.inventTable();
                break;

            case (tableNum(SMAServiceObjectRelation)) :
                serviceObjectRelation = _caller;
                if (serviceObjectRelation.TemplateBOMId && smaTemplateBOMTable)
                {
                    curInventTable = smaTemplateBOMTable.inventTable();
                }
                else
                {
                    curInventTable = null;
                }
                break;

            default :
                curInventTable = null;
                return;
        }

        setup_ds.write();
        setup_ds.refresh();
        designerCtrl.linkActive(_caller, setup);
        history.visible(!designerCtrl.isMasterMode());

        // Only activate date filters if history window is active
        filter.visible(history.visible());

        if (history.visible())
        {
            designerCtrl.listInitListView();
        }
    }
    catch (Exception::Error)
    {
        throw Exception::Error;
    }

    firstTime = false;
}

public void init()
{
    if (!element.args().caller() || !element.args().dataset())
    {
        // Form %1 requires an active buffer.
        throw error(strFmt("@SYS22678", element.name()));
    }

    allowCreate = true;
    firstTime   = true;

    super();

    designerCtrl = this.smabomDesignerCtrl();

    element.initObjectRelation();
    element.initSetupTab();
    element.initLeftView();
    element.initRightView();
    if (isConfigurationkeyEnabled(configurationKeyNum(SMA)))
    {
        EditBOMLine.visible(true);
    }
}

void initLeftView()
{
    boMtree.setImagelist(designerCtrl.imageList());

    boMtree.setStateImagelist(designerCtrl.imageListState());
}

void initObjectRelation()
{
    SMAServiceObjectRelation    objectRelation;

    if (element.args().dataset() == tableNum(SMAServiceObjectRelation))
    {
        objectRelation = element.args().record();
        if (objectRelation)
        {
            relationId = objectRelation.ServiceObjectRelationId;
            designerCtrl.parmSMAServiceObjectRelationId(relationId);
            designerCtrl.parmServiceObjectId(objectRelation.ServiceObjectId);
        }
    }
}

void initRightView()
{
    boolean activateHistory;
    boolean activateProduction;
    boolean activateInventoryItem;

    activateHistory = true;
    activateProduction = designerCtrl.accessProdTable() >= AccessType::View ? true : false;
    activateInventoryItem = designerCtrl.accessItemTable() >= AccessType::View ? true : false;

    rightGrp.visible(activateHistory || activateProduction || activateInventoryItem);

    if (rightGrp.visible())
    {
        history.visible(activateHistory);
        designerCtrl.listInitListView();
    }

    history.visible(activateProduction || activateInventoryItem);

    ProductionItemPage.visible(activateProduction);      
}

void initSetupTab()
{
    setUp_ds.create();
    setUp.data(designerCtrl.parmTmpSMABOMDesignerSetup());
    setUp.write();

    // Show edit dialog for added items
    setUp.AutoEdit = NoYes::Yes;

    designerCtrl.controlFormatExplain(setupFormatExplain);

    if (designerCtrl.accessServiceBOM() < AccessType::Delete)
    {
        confirmDelete.visible(false);
    }

    if (designerCtrl.accessServiceBOM() < AccessType::Add)
    {
        autoEdit.visible(false);
    }

    if (!confirmDelete.visible() && !autoEdit.visible())
    {
        setupConfirm.visible(false);
    }
}

public void refreshDesigner()
{
    try
    {
        element.design().heightValue(element.design().heightValue() - 1);
        element.design().heightValue(element.design().heightValue() + 1);
    }
    catch (Exception::Error)
    {
        throw Exception::Error;
    }
}

void setButtonPanel()
{
    boolean enabled;

    try
    {
        if (editBOMButton.visible())
        {
            enabled = designerCtrl.canEditMasterBOM();
            if (editBOMButton.enabled() != enabled)
            {
                editBOMButton.enabled(enabled);
            }
        }
        if (deleteBOMButton.visible())
        {
            enabled = designerCtrl.canDeleteMasterBOM();
            if (deleteBOMButton.enabled() != enabled)
            {
                deleteBOMButton.enabled(enabled);
            }
        }
    }
    catch (Exception::Error)
    {
        throw Exception::Error;
    }
}

void setDelayedControls(int _curMasterBOMNode = curMasterBOMNode, boolean _forced = false, boolean _executeQuery = designerCtrl.mustExecuteQuerySMAMasterBOM())
{
    if (!designerCtrl.mustUpdateDelayedInfo() && !_forced)
    {
        return;
    }

    try
    {
        element.setButtonPanel();
        designerCtrl.listSetHistory();

        if (_executeQuery)
        {
            smaServiceBOMTable_ds.executeQuery();
        }
    }
    catch (Exception::Error)
    {
        throw Exception::Error;
    }
}

void setDelayedSelectNodes()
{
    int i;

    if (!conLen(globalselectNodes))
    {
        return;
    }

    for (i = 1; i <= conLen(globalselectNodes); i++)
    {
        if (i == 1)
        {
            boMtree.select(conPeek(globalselectNodes, i));
        }
        else
        {
            boMtree.selectEx(conPeek(globalselectNodes, i), false);
        }
    }
    globalselectNodes = conNull();
}

SMABOMDesignerCtrl smabomDesignerCtrl()
{
    SMABOMDesignerCtrl  tmpDesignerCtrl = designerCtrl;

    if (!tmpDesignerCtrl)
    {
        tmpDesignerCtrl = SMABOMDesignerCtrl::construct(element, boMtree, historyList, element.args().dataset());
    }

    return tmpDesignerCtrl;
}

