display TransDate lineModifiedDate()
{
    SMAServiceBOMTable  lines;
    SMATemplateBOMId    template;

    template = designerCtrl.parmCallerSMAMasterBOMTable().TemplateBOMId;
    lines.recordLevelSecurity(true);

    if (designerCtrl.isMasterMode())
    {
        select firstonly ModifiedDateTime from lines order by ModifiedDateTime desc
            where lines.ServiceBOMId                == template &&
                  lines.ServiceObjectRelationId     == '';
    }
    else
    {
        select firstonly ModifiedDateTime from lines order by ModifiedDateTime desc
            where lines.ServiceBOMId                == template &&
                  lines.ServiceObjectRelationId     == designerCtrl.parmSMAServiceObjectRelationId();
    }

    return DateTimeUtil::date(DateTimeUtil::applyTimeZoneOffset(lines.ModifiedDateTime, DateTimeUtil::getUserPreferredTimeZone()));
}

