public void executeQuery()
{
    Integer         i = 0;
    ProdTable       prodTable;
    Query           q = new Query();

    q.addDataSource(tableNum(ProdTable));

    prodTable.ProdStatus = ProdStatus::Completed;
    prodTable.status().queryAddStatus(q);

    prodTable.ProdStatus = ProdStatus::ReportedFinished;
    prodTable.status().queryAddStatus(q);

    this.query(q);

    super();
}

public void linkActive()
{
    super();

    element.doLinkActive(element.args().record());

    designerCtrl.listSetHistory(true);
}

public void init()
{
    super();

    this.query().dataSourceTable(tableNum(SMAServiceBOMTable)).addRange(fieldNum(SMAServiceBOMTable, ServiceBOMLineId));
}

public void executeQuery()
{
    this.query().dataSourceTable(tableNum(SMAServiceBOMTable)).findRange(fieldNum(SMAServiceBOMTable, ServiceBOMLineId)).value(queryValue(designerCtrl.toolsNode2LineId(bomTree.getSelection())));

    super();
}

