public boolean expanding(int _idx, FormTreeExpand _action, anytype _data)
{
    if (_action == FormTreeExpand::Expand)
    {
        designerCtrl.treeExpandNode(_idx);
    }
    return super(_idx, _action, _data);
}

public void selectionChanged(FormTreeItem _oldItem, FormTreeItem _newItem, FormTreeSelect _how)
{
    if (_newItem.idx() != _oldItem.idx())
    {
        curMasterBOMNode = _newItem.idx();
        element.cancelTimeOut(curTimeOutHandle);
        designerCtrl.listSetHistory(true);
    }
}

public boolean modified()
{
    boolean ret;

    ret = super();

    designerCtrl.setFromDate(this.dateValue());

    designerCtrl.listSetHistory(true);

    return ret;
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (toDate.dateValue() != dateNull() && this.dateValue() != dateNull() && this.dateValue() > toDate.dateValue())
    {
        // From date must be before To date
        ret = ret && checkFailed("@SYS91020");
    }
    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    designerCtrl.setToDate(this.dateValue());

    designerCtrl.listSetHistory(true);

    return ret;
}

