/// <summary>
/// uses the global variable idx as a fallback when the tree looses focus
/// </summary>
public void clicked()
{
    int selectedIndex = BOMTree.getSelection();
    if (selectedIndex != 0)
    {
        idx = selectedIndex;
    }
    if (idx == 0)
    {
        checkFailed("@Proj:SelectBOMBranch");
    }
    else
    {
        designerCtrl.treeAddLines(prodTableDrag, idx);
    }
}

