[Form]
public class SMADispatchBoard extends FormRun
{
    ResAvailabilityViewContext context;

    QueryBuildRange qbrDispatchTeamFilter;
    QueryBuildRange qbrDispatchedFilter;
    QueryBuildRange qbrServiceOrderActivitiesFilter;
    QueryBuildRange qbrServiceOrderLinesFilter;
    QueryBuildRange qbrServiceOrderObjectsFilter;

}

public ResAvailabilityViewContext AvailabilityViewContext(ResAvailabilityViewContext _context = context)
{
    context = _context;
    return context;
}

/// <summary>
/// Availability view form initialization
/// </summary>
public void init()
{
    super();
    context = ResAvailabilityViewContext::construct();

    if (element.args() && element.args().caller())
    {
        ResAvailabilityViewContext parameter = element.args().caller() as ResAvailabilityViewContext;
        context = parameter ? parameter : context;
    }
    context.Timescale(AVTimeframeType::Day);
    this.populateData();
    context.showScheduleDescription(true);
    context.showRemainingCapacity(true);
    DispatchBoard.startDateTime(context.StartDateTime());
    DispatchBoard.endDateTime(context.EndDateTime());
    DispatchBoard.Timescale(context.Timescale());
    DispatchBoard.controlContext(context);
}

void populateData()
{
    GanttTmpSMA temp = SMADispatchBoardHelper::populateGanttTmpSMA(context.StartDateTime(), context.EndDateTime());
    GanttTmpSMA.setTmpData(temp);
}

void viewSettingsReady(ResAvailabilityViewSetting _viewSetting)
{
    context = DispatchBoard.controlContext();
    utcdatetime dateTime;
    AVTimeframeType timescale;
    boolean showContent;
    boolean hasChanged;
    ResCapacityAggregation aggregation;

    dateTime = _viewSetting.StartDateTime();
    if (context.StartDateTime() != dateTime)
    {
        context.StartDateTime(dateTime);
        DispatchBoard.startDateTime(dateTime);
        hasChanged = true;
    }

    dateTime = _viewSetting.EndDateTime();
    if (context.EndDateTime() != dateTime)
    {
        context.EndDateTime(dateTime);
        DispatchBoard.endDateTime(dateTime);
        hasChanged = true;
    }

    timescale = _viewSetting.Timescale();
    if (context.Timescale() != timescale)
    {
        context.Timescale(timescale);
        DispatchBoard.Timescale(timescale);
        hasChanged = true;
    }

    showContent = _viewSetting.ShowScheduleDescription();
    if (context.ShowScheduleDescription() != showContent)
    {
        context.ShowScheduleDescription(showContent);
        hasChanged = true;
    }

    showContent = _viewSetting.ShowScheduleCapacity();
    if (context.ShowRemainingCapacity() != showContent)
    {
        context.ShowRemainingCapacity(showContent);
        hasChanged = true;
    }

    aggregation = _viewSetting.CapacityAggregation();
    if (context.CapacityAggregation() != aggregation)
    {
        context.CapacityAggregation(aggregation);
        hasChanged = true;
    }

    if (hasChanged)
    {
        DispatchBoard.controlContext(context);
        this.populateData();
        GanttTmpSMA_ds.research(true);
        SMAServiceOrderLine_ds.research(true);
        SMAServiceObjectRelation_ds.research(true);
        DispatchBoard.refresh();
    }
}

void refreshBoard(AVActionCompletedEventData _eventData)
{
    element.populateData();
    ganttTmpSMA_ds.executeQuery();
    DispatchBoard.refresh();
}

/// <summary>
/// activities filter.
/// </summary>
public void init()
{
    super();

    qbrDispatchTeamFilter           = this.query().dataSourceTable(tablenum(GanttTmpSMA)).addRange(fieldnum(GanttTmpSMA, DispatchTeamId));
    qbrDispatchedFilter             = this.query().dataSourceTable(tablenum(GanttTmpSMA)).addRange(fieldnum(GanttTmpSMA, Dispatched));
    qbrServiceOrderActivitiesFilter = this.query().dataSourceTable(tablenum(GanttTmpSMA)).addRange(fieldnum(GanttTmpSMA, ServiceOrderId));
}

/// <summary>
/// Initializes service order lines filter.
/// </summary>
public void init()
{
    super();

    qbrServiceOrderLinesFilter     = this.query().dataSourceTable(tablenum(SMAServiceOrderLine)).addRange(fieldnum(SMAServiceOrderLine, ServiceOrderId));
    qbrServiceOrderLinesFilter.value(SysQuery::valueEmptyString());
}

public Common resolveReference(FormReferenceControl _formReferenceControl)
{
    Common             ret;

    ret = super(_formReferenceControl);

    if (ret.RecId && !HcmWorkerLookup::newOnlyActiveEmployeesWithinCompany().validateWorker(ret.RecId))
    {
        ret.clear();
    }

    return ret;
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    return HcmWorkerLookup::newOnlyActiveEmployeesWithinCompany().lookupWorker(_formReferenceControl);
}

/// <summary>
/// Initializes service object relation filter.
/// </summary>
public void init()
{
    super();

    qbrServiceOrderObjectsFilter     = this.query().dataSourceTable(tablenum(SMAServiceObjectRelation)).addRange(fieldnum(SMAServiceObjectRelation, RelKeyId));
    qbrServiceOrderObjectsFilter.value(SysQuery::valueEmptyString());
    qbrServiceOrderObjectsFilter.status(RangeStatus::Hidden);
}

public void clicked()
{
    super();
    SMADispatchBoardDataController dataController = SMADispatchBoardDataController::construct(DispatchBoard);
    dataController.actionCompleted += eventhandler(element.refreshBoard);
    dataController.executeAction(identifierStr(Assign));
}

/// <summary>
/// Refresh the availability view contents
/// </summary>
public void clicked()
{
    super();
    GanttTmpSMA temp = SMADispatchBoardHelper::populateGanttTmpSMA(context.StartDateTime(), context.EndDateTime());
    GanttTmpSMA.setTmpData(temp);
    GanttTmpSMA_ds.research(true);
    SMAServiceOrderLine_ds.research(true);
    SMAServiceObjectRelation_ds.research(true);
    DispatchBoard.refresh();
}

public void clicked()
{
    super();

    ResAvailabilityViewSetting viewSetting = ResAvailabilityViewSetting::construct();
    context = DispatchBoard.controlContext();
    viewSetting.StartDateTime(context.StartDateTime());
    viewSetting.EndDateTime(context.EndDateTime());
    viewSetting.Timescale(context.Timescale());
    viewSetting.CapacityToggleDescription("@Res:DisplayAllocationCapacityLabel");
    viewSetting.ShowScheduleCapacity(context.ShowRemainingCapacity());
    viewSetting.ShowScheduleDescription(context.ShowScheduleDescription());
    viewSetting.CapacityAggregation(context.CapacityAggregation());
    viewSetting.selectionsReady += eventhandler(element.viewSettingsReady);
    viewSetting.showForm();
}

public void pageActivated()
{
    super();

    qbrDispatchTeamFilter.value(SysQuery::valueUnlimited());
    qbrDispatchedFilter.value(SysQuery::valueUnlimited());
    qbrServiceOrderActivitiesFilter.value(SysQuery::valueUnlimited());
    ganttTmpSMA_ds.executeQuery();
}

public void pageActivated()
{
    HcmWorker   hcmWorker;

    super();

    hcmWorker = HcmWorker::find(HcmWorker::userId2Worker(curuserid()));
    qbrDispatchTeamFilter.value(queryValue(SMADispatchWorkerSetup::findByWorker(hcmWorker.RecId).DispatchTeamId));
    qbrDispatchedFilter.value(SysQuery::valueUnlimited());
    qbrServiceOrderActivitiesFilter.value(SysQuery::valueUnlimited());
    ganttTmpSMA_ds.executeQuery();
}

