public void pageActivated()
{
    super();

    qbrDispatchTeamFilter.value(SysQuery::valueUnlimited());
    qbrDispatchedFilter.value(SysQuery::valueUnlimited());
    qbrServiceOrderActivitiesFilter.value(ganttTmpSMA.ServiceOrderId);
    ganttTmpSMA_ds.executeQuery();
}

public void pageActivated()
{
    super();

    qbrServiceOrderLinesFilter.value(ganttTmpSMA.RecId ? ganttTmpSMA.ServiceOrderId : SysQuery::valueEmptyString());
    smaServiceOrderLine_ds.executeQuery();
}

public void pageActivated()
{
    super();

    qbrServiceOrderObjectsFilter.value(ganttTmpSMA.RecId ?
        SMAServiceObjectRelation::queryFilterValue(smaServiceObjectRelation_ds.query().dataSourceTable(tablenum(SMAServiceObjectRelation)), ganttTmpSMA.AgreementId, ganttTmpSMA.ServiceOrderId) :
        SysQuery::valueEmptyString());
    smaServiceObjectRelation_ds.executeQuery();
}

public void jumpRef()
{
    MenuFunction menuFunction = new MenuFunction(menuItemDisplayStr(SMAServiceObjectRelation), MenuItemType::Display);

    // Check access rights
    if (!menuFunction || !menuFunction.checkAccessRights())
    {
        // Access rights are not sufficient to use this function.
        throw error("@SYS81158");
    }

    Args args= new Args();
    args.caller(this);
    switch (smaServiceObjectRelation.RelTableId)
    {
        case(tableNum(SMAAgreementTable))       :
            args.record(SMAAgreementTable::find(smaServiceObjectRelation.RelKeyId));
            break;
        case(tableNum(SMAServiceOrderTable))    :
            args.record(SMAServiceOrderTable::find(smaServiceObjectRelation.RelKeyId));
            break;
        case(tableNum(SMAServiceObjectTable)) :
            args.record(SMAServiceObjectTable::find(smaServiceObjectRelation.RelKeyId));
            break;
        default:
            args.record(smaServiceObjectRelation);
            break;
    }

    menuFunction.run(args);
}
