void close()
{
    if (numberSeqFormHandler)
    {
        numberSeqFormHandler.formMethodClose();
    }

    super();
}

public void init()
{
    SMAServiceOrderTable    serviceOrderTable;
    SMAAgreementTable       agreementTable;
    SMAServiceObjectTable   objectTable;

    if (!element.args().dataset())
    {
        // Form '%1' must be called with caller.
        throw error(strFmt("@SYS84645", element.name()));
    }
    else
    {
        switch (element.args().dataset())
        {
            case(tableNum(SMAAgreementTable)) :
                agreementTable              = element.args().record();
                agreementId                 = agreementTable.AgreementId;
                agreementHasServiceOrders   = SMAAgreementTable::hasServiceOrders(agreementId);
                break;

            case(tableNum(SMAServiceOrderTable)) :
                serviceOrderTable   = element.args().record();
                serviceOrderId      = serviceOrderTable.ServiceOrderId;
                agreementId         = serviceOrderTable.AgreementId;
                break;

            case(tableNum(SMAServiceObjectTable)) :
                objectTable         = element.args().record();
                objectId            = objectTable.ServiceObjectId;
                break;

            case(tableNum(SMAAgreementLine)) :
                SMAAgreementLine line       = element.args().record();
                agreementId                 = line.AgreementId;
                agreementHasServiceOrders   = SMAAgreementTable::hasServiceOrders(agreementId);
                break;

            default:
                break;
        }

        callerTableId = element.args().dataset();
    }

    super();

    if (element.args().dataset() == tableNum(SMAServiceObjectTable) || element.args().dataset() == tableNum(CustTable))
    {
        smaServiceObjectRelation_ds.allowCreate(false);
        smaServiceObjectRelation_ds.allowDelete(false);
        smaServiceObjectRelation_ds.allowEdit(false);
    }

    element.updateDesign(InventDimFormDesignUpdate::Init);

    showServiceOrder    = false;
    showAgreement       = false;

    this.setButtonAccess();
}

public void run()
{
    super();

    if (element.args().dataset() == tableNum(SMAAgreementLine))
    {
        SMAAgreementLine line = element.args().record();
        this.dataSource().findRecord(SMAServiceObjectRelation::find(line.ServiceObjectRelationId));
    }
}

