public void refreshDatasource()
{
    SMAServiceObjectRelation relation;

    // Save the current service object relation
    relation = smaServiceObjectRelation.data();

    // Refresh data source
    smaServiceObjectRelation_ds.research();

    // Reselect the service object relation
    if (relation)
    {
        smaServiceObjectRelation_ds.positionToRecord(relation);
    }
}

public void rereadDatasource()
{
    smaServiceObjectRelation_ds.reread();
    smaServiceObjectRelation_ds.refresh();

    this.setButtonAccess();
}

void setButtonAccess()
{
    boolean isServiceBOM;
    boolean existRecord;

    existRecord     = smaServiceObjectRelation.RecId            != 0;
    isServiceBOM    = smaServiceObjectRelation.TemplateBOMId    != '';

    // Repair button
    //Repair button only enabled if service objects and when called with a service order or called with an agreement that has service orders
    smaRepairLine.enabled(existRecord && (serviceOrderId || agreementHasServiceOrders));
    // Service BOM buttons
    editServiceBOM.enabled(isServiceBOM);
    smaServiceBOMAction_Attach.enabled(!isServiceBOM);
    smaServiceBOMAction_Delete.enabled(isServiceBOM);
    smaServiceBOMAction_Move.enabled(isServiceBOM);

    // Is form called with a service order and the object relation type is agreement, the function button should be disabled
    if (serviceOrderId && smaServiceObjectRelation.RelTableId == tableNum(SMAAgreementTable))
    {
        functions.enabled(false);
    }
    else
    {
        functions.enabled(existRecord);
    }

    // Function button
    functions.enabled(existRecord);
}

boolean setFieldAccess()
{
    boolean showObject;

    showObject = !(showAgreement || showServiceOrder);

    // Show correct fields on Overview tab
    grid_ServiceObjectId.visible(showObject);
    grid_M_objectDescription.visible(showObject);

    grid_RelKeyId.visible(!showObject);
    grid_M_showRelationDescription.visible(!showObject);

    if (showAgreement)
    {
        grid_RelKeyId.label("@SYS79078"); // Agreement
        relation_RelKeyId.label("@SYS79078"); // Agreement
    }

    if (showServiceOrder)
    {
        grid_RelKeyId.label("@SYS79077"); // Service Order
        relation_RelKeyId.label("@SYS79077"); // Service Order
    }

    smaServiceObjectRelation_ds.object(fieldNum(SMAServiceObjectRelation, RelKeyId)).enabled(!showObject);

    return true;
}

void updateDesign(InventDimFormDesignUpdate _mode)
{
    InventDimParm   inventDimParm;
    boolean         existItemId;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup  = InventDimCtrl::newFromForm(element);
            }

        case InventDimFormDesignUpdate::Active, InventDimFormDesignUpdate::FieldChange :
            inventDimParm = EcoResProductDimGroupSetup::newItemId(SMAServiceObjectTable::find(smaServiceObjectRelation.ServiceObjectId).ItemId).inventDimParmActiveDimensions();

            inventDimFormSetup.parmSkipOnHandLookUp(true);
            inventDimFormSetup.parmDimParmEnabled(inventDimParm);

            inventDimFormSetup.formSetControls(true);
            break;

        default :
            // Incorrect parameter for the function '%1'
            throw error(strFmt("@SYS54195",funcName()));
    }

    existItemId = SMAServiceObjectTable::find(smaServiceObjectRelation.ServiceObjectId).ItemId != '';
}

public void updateInventDimRecord()
{
    inventDim.data(InventDim::find(smaServiceObjectRelation.InventDimId));
}

/// <summary>
/// Initializes form based on service object and the relation type.
/// </summary>
/// <param name = "_queryBuildDataSource">The <c>SMAServiceObjectRelation</c> data source object.</param>
protected void initializeFromServiceObjectRelationType(QueryBuildDataSource _queryBuildDataSource)
{
    switch (element.args().parmEnum())
    {
        case SMARelationType::Agreement:
            tableIdRange = _queryBuildDataSource.addRange(fieldNum(SMAServiceObjectRelation, RelTableId));
            tableIdRange.value(queryValue(tableNum(SMAAgreementTable)));
            showAgreement       = true;
            showServiceOrder    = false;
            break;

        case SMARelationType::ServiceOrder:
            tableIdRange = _queryBuildDataSource.addRange(fieldNum(SMAServiceObjectRelation, RelTableId));
            tableIdRange.value(queryValue(tableNum(SMAServiceOrderTable)));
            showServiceOrder    = true;
            showAgreement       = false;
            break;

        default:
            showServiceOrder    = false;
            showAgreement       = false;
            break;
    }
}

public int active()
{
    int ret = super();

    inventDim.data(InventDim::find(smaServiceObjectRelation.InventDimId));

    element.updateDesign(InventDimFormDesignUpdate::Active);

    if (!(showAgreement || showServiceOrder))
    {
        switch (smaServiceObjectRelation.RelTableId)
        {
            case (tableNum(SMAAgreementTable))    :
                grid_RelKeyId.label("@SYS79078"); // Agreement
                relation_RelKeyId.label("@SYS79078"); // Agreement
                break;

            case (tableNum(SMAServiceOrderTable)) :
                grid_RelKeyId.label("@SYS79077"); // Service Order
                relation_RelKeyId.label("@SYS79077"); // Service Order
                break;

            default:
                break;
        }
    }

    if (smaServiceObjectRelation.SalesId)
    {
        inventDim_ds.allowEdit(false);
    }
    else
    {
        inventDim_ds.allowEdit(true);
    }

    element.setButtonAccess();

    return ret;
}

