}

public void init()
{
    super();

    Query query = new Query();
    QueryBuildDataSource qbds = query.addDataSource(tableNum(SMAServiceObjectRelation));

    if (element.args().dataset() == tableNum(SMAAgreementTable) || element.args().dataset() == tableNum(SMAAgreementLine))
    {
        keyIdRange = qbds.addRange(fieldNum(SMAServiceObjectRelation, RelKeyId));
        keyIdRange.value(agreementId);

        tableIdRange = qbds.addRange(fieldNum(SMAServiceObjectRelation, RelTableId));
        tableIdRange.value(queryValue(tableNum(SMAAgreementTable)));
    }
    else
    if (element.args().dataset() == tableNum(SMAServiceOrderTable))
    {
        keyIdRange = qbds.addRange(fieldNum(SMAServiceObjectRelation, RelKeyId));
        keyIdRange.value(SMAServiceObjectRelation::queryFilterValue(qbds, agreementId, serviceOrderId));
        keyIdRange.status(RangeStatus::Hidden);
    }
    else
    if (element.args().dataset() == tableNum(SMAServiceObjectTable))
    {
        objectIdRange = qbds.addRange(fieldNum(SMAServiceObjectRelation, ServiceObjectId));
        objectIdRange.value(objectId);

        element.initializeFromServiceObjectRelationType(qbds);
    }
    else
    if (element.args().dataset() == tableNum(CustTable))
    {
        qbds.orderMode(OrderMode::OrderBy);
        qbds.addSortField(fieldNum(SMAServiceObjectRelation, ServiceObjectId));

        QueryBuildDataSource qbdsAgreement = qbds.addDataSource(tableNum(SMAAgreementTable));
        qbdsAgreement.relations(true);
        qbdsAgreement.joinMode(JoinMode::ExistsJoin);

        QueryBuildDataSource qbdsProject = qbdsAgreement.addDataSource(tableNum(ProjTable));
        qbdsProject.relations(true);

        custRange = qbdsProject.addRange(fieldNum(ProjTable, CustAccount));
        custRange.value(custAccount);

        // show objects only
        showServiceOrder    = false;
        showAgreement       = false;
    }

    this.query(query);
    element.setFieldAccess();
}

public void initValue()
{
    super();

    switch (element.args().dataset())
    {
        case tableNum(SMAServiceOrderTable):
            smaServiceObjectRelation.RelTableId = tableNum(SMAServiceOrderTable);
            smaServiceObjectRelation.RelKeyId   = serviceOrderId;
            break;

        case tableNum(SMAAgreementTable):
            smaServiceObjectRelation.RelTableId = tableNum(SMAAgreementTable);
            smaServiceObjectRelation.RelKeyId   = agreementId;
            break;

        case tableNum(SMAAgreementLine):
            smaServiceObjectRelation.RelTableId = tableNum(SMAAgreementTable);
            smaServiceObjectRelation.RelKeyId   = agreementId;
            break;

        default:
            break;
    }
}

public void linkActive()
{
    SMAAgreementTable       agreementTable;
    SMAServiceOrderTable    serviceOrderTable;
    SMAServiceObjectTable   serviceObjectTable;
    CustTable               custTable;

    switch (element.args().dataset())
    {
        case(tableNum(SMAAgreementTable)):
            agreementTable  = element.args().record();
            agreementId     = agreementTable.AgreementId;
            keyIdRange.value(agreementId);
            break;

        case(tableNum(SMAServiceOrderTable)):
            serviceOrderTable   = element.args().record();
            serviceOrderId      = serviceOrderTable.ServiceOrderId;
            agreementId         = serviceOrderTable.AgreementId;
            keyIdRange.value(SMAServiceObjectRelation::queryFilterValue(keyIdRange.dataSource(), agreementId, serviceOrderId));
            break;

        case(tableNum(SMAServiceObjectTable)):
            serviceObjectTable = element.args().record();
            objectId = serviceObjectTable.ServiceObjectId;
            objectIdRange.value(objectId);
            break;

        case(tableNum(CustTable)):
            custTable = element.args().record();
            custAccount = custTable.AccountNum;
            custRange.value(custAccount);
            break;

        case(tableNum(SMAAgreementLine)):
            SMAAgreementLine line       = element.args().record();
            agreementId                 = line.AgreementId;
            keyIdRange.value(agreementId);
            break;

        default:
            break;
    }

    element.numberSeqFormHandler().formMethodDataSourceLinkActive();

    super();
}

public boolean validateDelete()
{
    SMAAgreementLine            checkRelation;
    SMAServiceOrderLine         checkServiceOrder;
    boolean                     ret = true;

    switch (smaServiceObjectRelation.RelTableId)
    {
        case tableNum(SMAAgreementTable):
            if (callerTableId ==  tableNum(SMAServiceOrderTable))
            {
                // Cannot delete service object attached to the agreement.
                ret = checkFailed("@SYS99265");
            }
            if (ret)
            {
                select firstonly checkRelation where checkRelation.AgreementId == smaServiceObjectRelation.RelKeyId && checkRelation.ServiceObjectRelationId == smaServiceObjectRelation.ServiceObjectRelationId;

                if (checkRelation)
                {
                    // Service object relation is used on service agreement lines.
                    ret = checkFailed("@SYS87859");
                }
            }
            break;

        case tableNum(SMAServiceOrderTable):
            select firstonly checkServiceOrder where checkServiceOrder.ServiceOrderId == smaServiceObjectRelation.RelKeyId && checkServiceOrder.ServiceObjectRelationId == smaServiceObjectRelation.ServiceObjectRelationId;
            if (checkServiceOrder)
            {
                // Service object relation is used on service order lines.
                ret = checkFailed("@SYS89070");
            }
            break;

        default:
            break;
    }

    if (ret)
    {
        ret = super();
    }

    return ret;
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    ret = element.numberSeqFormHandler().formMethodDataSourceValidateWrite(ret) && ret;

    return ret;
}

public void write()
{
    if (SMAServiceObjectTable::find(smaServiceObjectRelation.ServiceObjectId).ItemId)
    {
        smaServiceObjectRelation.InventDimId = SMAServiceObjectTable::find(smaServiceObjectRelation.ServiceObjectId).InventDimId;
    }
    else
    {
        smaServiceObjectRelation.InventDimId = InventDim::inventDimIdBlank();
    }

    ttsbegin;

    element.numberSeqFormHandler().formMethodDataSourceWrite();

    super();

    ttscommit;
}

