public void modified()
{
    SMAServiceObjectTable   smaServiceObjectTable;

    super();

    smaServiceObjectTable = SMAServiceObjectTable::find(smaServiceObjectRelation.ServiceObjectId);
    if (smaServiceObjectTable.ServiceObjectId)
    {
        smaServiceObjectRelation.InventDimId = smaServiceObjectTable.InventDimId;
    }
    else
    {
        smaServiceObjectRelation.InventDimId = InventDim::inventDimIdBlank();
    }

    element.updateInventDimRecord();
}

public void jumpRef()
{
    FormRun formRun;
    Args args = new Args();
    args.name(formStr(SMAServiceObjectRelation));
    args.caller(this);

    if (smaServiceObjectRelation.RelTableId == tableNum(SMAServiceOrderTable)
        || smaServiceObjectRelation.RelTableId == tableNum(SMAAgreementTable))
    {
        if (smaServiceObjectRelation.RelTableId == tableNum(SMAAgreementTable))
        {
            args.record(SMAAgreementTable::find(smaServiceObjectRelation.RelKeyId));
        }
        else
        {
            args.record(SMAServiceOrderTable::find(smaServiceObjectRelation.RelKeyId));
        }

        // Menu item info will be used by security framework, when setting access rights to form data sources
        args.menuItemType(MenuItemType::Display);
        args.menuItemName(menuitemDisplayStr(SMAServiceObjectRelation));

        formRun = ClassFactory::formRunClassOnClient(args);
        formRun.init();
        formRun.run();
        formRun.wait();
    }
}

