/// <summary>
/// Specifies whether the project currency code can be edited.
/// </summary>
void enableProjSalesCurrencyId()
{
    if (ProjInvoiceTable::isSalesCurrencyLocked(ProjTable::find(smaServiceOrderTable.ProjId).ProjInvoiceProjId))
    {
        smaServiceOrderline_ds.object(fieldNum(SMAServiceOrderLine, ProjCurrencyCode)).allowEdit(false);
        smaServiceOrderline_ds.object(fieldNum(SMAServiceOrderLine, ProjCurrencyCode)).skip(true);
    }
    else
    {
        smaServiceOrderline_ds.object(fieldNum(SMAServiceOrderLine, ProjCurrencyCode)).allowEdit(true);
        smaServiceOrderline_ds.object(fieldNum(SMAServiceOrderLine, ProjCurrencyCode)).skip(false);
    }
}

void executeQueryHeader()
{
    smaServiceOrderTable_ds.executeQuery();
}

void executeQueryLines()
{
    if (SMAServiceOrderTableDSResearchFlight::instance().isEnabled())
    {
        SmaServiceOrderLine smaServiceOrderLineCur;

        smaServiceOrderLineCur.data(smaServiceOrderLine_ds.cursor());
        smaServiceOrderLine_ds.executeQuery();

        if (smaServiceOrderLineCur)
        {
            smaServiceOrderLine_ds.positionToRecord(smaServiceOrderLineCur);
        }
    }
    else
    {
        smaServiceOrderLine_ds.executeQuery();
    }
}

public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    List exportOptions = _menuOptions.customMenuItems();

    var exportOption = OfficeGeneratedExportMenuItem::construct(tableStr(SMAServiceOrderLineEntity), ExportServiceOrderHeaderAndLinesToExcelOptionId);
    exportOption.displayName(strfmt("@SYS76681", "@ServiceManagement:SMAServiceOrderHeaderAndLines", '', curExt()));
    exportOptions.addEnd(exportOption);
}

public ExportToExcelDataEntityContext getDataEntityContext(OfficeGeneratedExportMenuItem _menuItem)
{
    ExportToExcelDataEntityContext context = null;

    switch (_menuItem.id())
    {
        case ExportServiceOrderHeaderAndLinesToExcelOptionId:
            context = ExportToExcelDataEntityContext::constructDefault(dataentityviewstr(SMAServiceOrderHeaderEntity));

            var serviceOrderHeaderEntityInfo = context.findEntityByName(dataentityviewstr(SMAServiceOrderHeaderEntity));

            if (serviceOrderHeaderEntityInfo)
            {
                var serviceOrderHeaderFilterBuilder = new ExportToExcelFilterTreeBuilder(dataentityviewstr(SMAServiceOrderHeaderEntity));
                var serviceOrderHeaderIdFilter = serviceOrderHeaderFilterBuilder.areEqual(fieldStr(SMAServiceOrderHeaderEntity, ServiceOrderNumber), SMAServiceOrderTable.ServiceOrderId);

                serviceOrderHeaderEntityInfo.filter(serviceOrderHeaderFilterBuilder.and(serviceOrderHeaderFilterBuilder.companyFilter(), serviceOrderHeaderIdFilter));
            }

            var serviceOrderLineEntityInfo = context.addEntityDefault(dataentityviewstr(SMAServiceOrderLineEntity));
            var serviceOrderLineFilterBuilder = new ExportToExcelFilterTreeBuilder(dataentityviewstr(SMAServiceOrderLineEntity));
            var serviceOrderLineIdFilter = serviceOrderLineFilterBuilder.areEqual(fieldStr(SMAServiceOrderLineEntity, ServiceOrderNumber), SMAServiceOrderTable.ServiceOrderId);
            serviceOrderLineEntityInfo.filter(serviceOrderLineFilterBuilder.and(serviceOrderLineFilterBuilder.companyFilter(), serviceOrderLineIdFilter));
            break;
    }

    return context;
}

/// <summary>
/// Initializes the form
/// </summary>
public void init()
{
    SMAAgreementTable       agreementRecord;
    SMAParameters           parameters = SMAParameters::find();
    FormDataObject          fieldProjTaxGroup;
    FormDataObject          fieldProjTaxItemGroup;
    FormDataObject          fieldTaxGroupExpense;
    FormDataObject          fieldTaxItemGroupExpense;
    boolean                 isGTEEnabled = TaxSolutionScopeIntegrationUtil::isCompanyEnabled();

    ProjJournalName::find(parameters.ProjJournalHour);

    timeRegistrationVisible     = ProjParameters::find().TimeRegistration;
    timeRegistrationEnable      = ProjJournalName::find(parameters.ProjJournalHour).TimeRegistration;

    mandatoryAgreement  = !parameters.UnattachedServiceOrder;

    super();

    // Check if form is called from suspended service agreement then disable create
    isCallerSuspened = false;
    if (element.args() && element.args().caller())
    {
        if (element.args().dataset() == tableNum(SMAAgreementTable))
        {
            agreementRecord = element.args().record();
            if (agreementRecord)
            {
                isCallerSuspened = agreementRecord.Suspended == NoYes::Yes;
                smaServiceOrderTable_ds.allowCreate(!isCallerSuspened);
                smaServiceOrderLine_ds.allowCreate(!isCallerSuspened);
            }
        }
    }

    smaServiceOrderTable_ds.object(fieldNum(SMAServiceOrderTable, AgreementId)).mandatory(mandatoryAgreement);

    element.updateDesign(InventDimFormDesignUpdate::Init);
    //<GIN>
    isIndiaIndirectTaxParameterMarked = TaxParameters::checkTaxParameters_IN();
    isIndiaWHTMarked = TaxWithholdParameters_IN::checkTaxParameters();

    element.updateDesign_IN();
    // </GIN>

    this.setDSAccess();
    // Add button rules
    this.setButtonAccess();

    if (!SMAStageTable::existsStages())
    {
        smaServiceOrderTable_ds.allowCreate(false);
        element.enableDisableEditButton(false);
        // A warning already exists for this
    }
    else
    {
        ExpenseCost_LedgerDimension.parmPostingType(LedgerPostingType::LedgerJournal);

        hcmWorkerLookupCurrentCompany = HcmWorkerLookup::newCustomOptions(true, true, true, false, true, true, true, true, true);

        // Needed to check validity of address
        logisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidFrom));
        logisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidTo));
    }

    // <GTE>
    if (TaxIntegrationUtils::isTaxInformationEnabled())
    {
        TransTaxInformation.visible(true);
    }

    fieldProjTaxGroup = SMAServiceOrderLine_ds.object(fieldNum(SMAServiceOrderLine, ProjTaxGroup));
    fieldProjTaxItemGroup = SMAServiceOrderLine_ds.object(fieldNum(SMAServiceOrderLine, ProjTaxItemGroup));
    fieldTaxGroupExpense = SMAServiceOrderLine_ds.object(fieldNum(SMAServiceOrderLine, TaxGroupExpense));
    fieldTaxItemGroupExpense = SMAServiceOrderLine_ds.object(fieldNum(SMAServiceOrderLine, TaxItemGroupExpense));

    fieldProjTaxGroup.visible(!isGTEEnabled);
    fieldProjTaxItemGroup.visible(!isGTEEnabled);
    fieldTaxGroupExpense.visible(!isGTEEnabled);
    fieldTaxItemGroupExpense.visible(!isGTEEnabled);
    // <GTE>
}

public void initFieldExpense()
{
    if (smaServiceOrderLine.TransactionType == SMATransactionType::Expense)
    {
        smaServiceOrderLine.CurrencyIdCost = CompanyInfoHelper::standardCurrency();
        smaServiceOrderLine.setDefaultOffsetAccount();
    }
    else
    {
        smaServiceOrderLine.OffsetAccountTypeExpense    = LedgerJournalACType::Ledger;
        smaServiceOrderLine.parmLedgerDimension(0);
        smaServiceOrderLine.CurrencyIdCost              = '';
        smaServiceOrderLine.InvoiceId                   = '';
    }
}

