void selectMoveLines(SMAServiceOrderLine _line, boolean _select)
{
    if (_select)
    {
        // Add record to value container
        contLineMarked += [_line.RecId];
    }
    else
    {
        // Remove record from value container
        contLineMarked = conDel(contLineMarked, conFind(contLineMarked, _line.RecId), 1);
    }
}

