void setDSAccess()
{
    boolean             allowEdit;

    allowEdit = SMAStageTable::find(smaServiceOrderTable.StageId).StageCanModify;
    smaServiceOrderTable_ds.allowEdit(allowEdit);

    smaServiceOrderLine_ds.allowEdit(allowEdit);
    smaServiceOrderLine_ds.allowCreate(allowEdit);
    smaServiceOrderLine_ds.allowDelete(allowEdit);
}

void setFieldAccess(SMATransactionType _transactionType)
{
    boolean showItem            = false;
    boolean showHour            = false;
    boolean showFee             = false;
    boolean showExpense         = false;

    switch (_transactionType)
    {
        case SMATransactionType::Hour:
            showHour = true;
            break;

        case SMATransactionType::Expense:
            showExpense = true;
            break;

        case SMATransactionType::Fee:
            showFee = true;
            break;

        case SMATransactionType::Item:
            showItem = true;
            break;
    }

    // Qty field
    smaServiceOrderLine_ds.object(fieldNum(SMAServiceOrderLine, Qty)).allowEdit(showHour || showItem || showExpense);

    // Hour and Item specific fields
    boolean showActivityIdFeatureEnabled = FeatureStateProvider::isFeatureEnabled(SMAAgreementAndServiceOrderItemTypeShowActivityIdFeature::instance());
    smaServiceOrderLine_ds.object(fieldNum(SMAServiceOrderLine, ActivityNumber)).visible(showHour || (showItem && showActivityIdFeatureEnabled));
    smaServiceOrderLine_ds.object(fieldNum(SMAServiceOrderLine, ActivityNumber)).allowEdit(showHour || (showItem && showActivityIdFeatureEnabled));

    // Item specific fields
    smaServiceOrderLine_ds.object(fieldNum(SMAServiceOrderLine, ItemId)).allowEdit(!smaServiceOrderLine.RecId && showItem);
    lineProjIndentification_ItemId.visible(showItem);

    smaServiceOrderLine_ds.object(fieldNum(SMAServiceOrderLine, InventDimId)).visible(showItem);
    smaServiceOrderLine_ds.object(fieldNum(SMAServiceOrderLine, InventDimId)).allowEdit(showItem);

    smaServiceOrderLine_ds.object(fieldNum(SMAServiceOrderLine, Unit)).visible(showItem);

    smaServiceOrderLine_ds.object(fieldNum(SMAServiceOrderLine, ProjCostPrice)).visible(showExpense || showHour || showItem);
    smaServiceOrderLine_ds.object(fieldNum(SMAServiceOrderLine, ProjCostPrice)).allowEdit(showExpense || showHour);

    // Expense specific fields
    expenseCost.visible(showExpense);
    smaServiceOrderLine_ds.object(fieldNum(SMAServiceOrderLine, InvoiceId)).visible(showExpense);

    // Controls
    projectCostPrice_M_costAmount.visible(showItem || showExpense);
    projectCostPrice.visible(!showFee);

    // Groups
    serviceTime.visible(showHour);
    timesheet.visible(timeRegistrationVisible && showHour);
    tabLineDimension.visible(showItem);

    // Activity
    dispatchActivity.visible(showHour);
    smaServiceOrderLine_ds.object(fieldNum(SMAServiceOrderLine, ActivityId)).visible(showHour);

    // Set mandatory fields for expense
    element.setFieldExpense(showExpense);
    element.setFieldAccessInvoice();

    if (smaServiceOrderLine.TransactionType == SMATransactionType::Item)
    {
        element.updateDesign(InventDimFormDesignUpdate::FieldChange);
    }

    if (showHour)
    {
        // Hours
        projectCostPrice_Qty.label("@SYS1151");
    }
    else
    {
        // Quantity
        projectCostPrice_Qty.label(smaServiceOrderLine_ds.object(fieldNum(SMAServiceOrderLine, Qty)).labelText());
    }

    tabLineDimension.visible(showItem);
}

void setFieldAccessHeader()
{
    SMAServiceOrderTableButtonStateProvi    buttonStateProvider = SMAServiceOrderTableButtonStateProvi::construct();
    boolean                                 allowEdit     = false;
    boolean                                 editProject   = false;
    boolean                                 editAgreement = false;
    boolean                                 editAddress;

    buttonStateProvider.setDatasources(smaServiceOrderTable, smaServiceOrderLine);

    allowEdit       = SMAStageTable::find(smaServiceOrderTable.StageId).StageCanModify;
    editAgreement   = smaServiceOrderTable.AgreementId == '' && smaServiceOrderTable.Origin == SMAServiceOrderOrigin::User;
    editProject     = smaServiceOrderTable.ProjId == '';
    editAddress     = buttonStateProvider.checkRuleLogisticsLocationSelect();

    smaServiceOrderTable_ds.object(fieldNum(SMAServiceOrderTable, CustAccount)).allowEdit(allowEdit);
    smaServiceOrderTable_ds.object(fieldNum(SMAServiceOrderTable, AgreementId)).allowEdit(editAgreement);
    smaServiceOrderTable_ds.object(fieldNum(SMAServiceOrderTable, ProjId)).allowEdit(editProject);
    smaServiceOrderTable_ds.object(fieldNum(SMAServiceOrderTable, ServicePostalAddress)).allowEdit(editAddress);
    smaServiceOrderTable_ds.object(fieldNum(SMAServiceOrderTable, ServiceAddressName)).allowEdit(editAddress);
    actionPaneAddressHeader.enabled(editAddress);
}

public void setFieldAccessInvoice()
{
    smaServiceOrderLine_ds.object(fieldNum(SMAServiceOrderLine, InvoiceId)).allowEdit(smaServiceOrderLine.OffsetAccountTypeExpense == LedgerJournalACType::Vend);
}

public void setFieldExpense(boolean _showExpense)
{
    boolean invoiceIdRequired = false;
    boolean offsetAccRequired = false;

    if (_showExpense && smaServiceOrderLine.SignOff)
    {
        invoiceIdRequired = smaServiceOrderLine.invoiceIdRequired();
        offsetAccRequired = true;
    }

    // Virtual mandatory fields
    smaServiceOrderLine_ds.object(fieldNum(SMAServiceOrderLine, InvoiceId)).mandatory(invoiceIdRequired);
    smaServiceOrderLine_ds.object(fieldNum(SMAServiceOrderLine, OffsetAccountTypeExpense)).mandatory(offsetAccRequired);
    smaServiceOrderLine_ds.object(fieldNum(SMAServiceOrderLine, LedgerDimension)).mandatory(offsetAccRequired);
}

