void updateDesign(InventDimFormDesignUpdate _mode)
{
    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init :
            if (!inventDimFormSetup)
            {
                inventDimFormSetup = InventDimCtrl_Frm_ServiceMgmt::newFromForm(element);
            }

        case InventDimFormDesignUpdate::Active, InventDimFormDesignUpdate::FieldChange :
            inventDimFormSetup.formActiveSetup(InventDimGroupSetup::newItemId(smaServiceOrderLine.ItemId));
            inventDimFormSetup.formSetControls(true);
            break;

        default :
            // Incorrect parameter for the function '%1'
            throw error(strFmt("@SYS54195", funcName()));
    }
}

public void updateDesign_IN()
{
    maximumRetailPrice_IN.visible(false);
    assessableValue_IN.visible(true);

    if (smaServiceOrderLine.TransactionType == SMATransactionType::Item)
    {
        maximumRetailPrice_IN.visible(true);
        withholdingTax_IN.visible(true);

        smaServiceOrderLineTaxExtension_IN_ds.object(fieldNum(SMAServiceOrderLineTaxExtension_IN, MaximumRetailPrice)).visible(TaxParameters::find().vat_in      == NoYes::Yes ||
                                                                                                  TaxParameters::find().SalesTax_IN   == NoYes::Yes ||
                                                                                                  TaxParameters::find().Excise_IN     == NoYes::Yes ||
                                                                                                  TaxParameters::find().Customs_IN    == NoYes::Yes ||
                                                                                                  TaxParameters::find().ServiceTax_IN == NoYes::Yes);

        smaServiceOrderLineTaxExtension_IN_ds.object(fieldNum(SMAServiceOrderLineTaxExtension_IN, AssessableValueTransactionCurrency)).visible( TaxParameters::find().vat_in        == NoYes::Yes ||
                                                                                                  TaxParameters::find().SalesTax_IN   == NoYes::Yes ||
                                                                                                  TaxParameters::find().Excise_IN     == NoYes::Yes ||
                                                                                                  TaxParameters::find().Customs_IN    == NoYes::Yes ||
                                                                                                  TaxParameters::find().ServiceTax_IN == NoYes::Yes);

        smaServiceOrderLineTaxExtension_IN_ds.object(fieldNum(SMAServiceOrderLineTaxExtension_IN, tdsGroup)).visible(TaxWithholdParameters_IN::find().tdsActivate == NoYes::Yes &&
                                                                                          TaxWithholdParameters_IN::find().tdsInvoice  == NoYes::Yes);

        smaServiceOrderLineTaxExtension_IN_ds.object(fieldNum(SMAServiceOrderLineTaxExtension_IN, tcsGroup)).visible(TaxWithholdParameters_IN::find().tcsActivate == NoYes::Yes &&
                                                                                          TaxWithholdParameters_IN::find().tcsInvoice  == NoYes::Yes);
    }

    if (smaServiceOrderLine.TransactionType == SMATransactionType::Hour)
    {
        assessableValue_IN.visible(true);
        smaServiceOrderLineTaxExtension_IN_ds.object(fieldNum(SMAServiceOrderLineTaxExtension_IN, AssessableValueTransactionCurrency)).visible(true);
    }

    if (smaServiceOrderLine.TransactionType == SMATransactionType::Expense)
    {
        assessableValue_IN.visible(true);
        withholdingTax_IN.visible(true);
        smaServiceOrderLineTaxExtension_IN_ds.object(fieldNum(SMAServiceOrderLineTaxExtension_IN, AssessableValueTransactionCurrency)).visible(true);
        smaServiceOrderLineTaxExtension_IN_ds.object(fieldNum(SMAServiceOrderLineTaxExtension_IN, tdsGroup)).visible(true);
        smaServiceOrderLineTaxExtension_IN_ds.object(fieldNum(SMAServiceOrderLineTaxExtension_IN, tcsGroup)).visible(false);
    }

    if (smaServiceOrderLine.TransactionType == SMATransactionType::Fee)
    {
        assessableValue_IN.visible(true);
        withholdingTax_IN.visible(true);
        smaServiceOrderLineTaxExtension_IN_ds.object(fieldNum(SMAServiceOrderLineTaxExtension_IN, AssessableValueTransactionCurrency)).visible(true);
        smaServiceOrderLineTaxExtension_IN_ds.object(fieldNum(SMAServiceOrderLineTaxExtension_IN, tdsGroup)).visible(true);
        smaServiceOrderLineTaxExtension_IN_ds.object(fieldNum(SMAServiceOrderLineTaxExtension_IN, tcsGroup)).visible(true);
    }
}

public void init()
{
    super();

    smaServiceOrderTable_ds.cacheAddMethod(tableMethodStr(SMAServiceOrderTable, serviceLevelAgreementStartDateTime));
    smaServiceOrderTable_ds.cacheAddMethod(tableMethodStr(SMAServiceOrderTable, editContactPersonName));
    logisticsPostalAddress_ds.validTimeStateUpdate(ValidTimeStateUpdate::Correction);
    logisticsPostalAddress_ds.query().validTimeStateDateTimeRange(DateTimeUtil::minValue(), DateTimeUtil::maxValue());
}

public int active()
{
    int ret;
    // Imposible to edit transferred lines
    smaServiceOrderTable_ds.allowEdit(smaServiceOrderTable.Progress != SMAServiceOrderProgress::Transferred);

    // It is not possible to select contact persons for agreement associated with project without customer account
    editContactPersonName.enabled(smaServiceOrderTable.CustAccount != '');

    ret = super();

    element.setFieldAccessHeader();

    element.setButtonAccess();

    element.setDSAccess();

    element.enableProjSalesCurrencyId();
    smaServiceOrderTable_ds.rereadReferenceDataSources();
    LogisticsLocationEntity::showHideEffectiveAddressMessageBar(element, logisticsPostalAddress);

    element.refreshActivities();
    return ret;
}

public void create(boolean _append = false)
{
    //The call to checkNumberSeq() takes time so I am saving it
    if (!isNumberSequence)
    {
        isNumberSequence =SMAServiceOrderTable::checkNumberSeq();
    }
    if (isNumberSequence)
    {
        element.numberSeqFormHandler().formMethodDataSourceCreatePre();

        super(_append);

        element.numberSeqFormHandler().formMethodDataSourceCreate();
    }
}

public void delete()
{
    element.numberSeqFormHandler().formMethodDataSourceDelete();

    super();

    element.setButtonAccess();
}

public void initValue()
{
    ProjTable callerProjTable;
    LogisticsPostalAddress  postalAddress;

    super();

    smaServiceOrderTable.Origin = SMAServiceOrderOrigin::User;

    if (smaServiceOrderTable.AgreementId)
    {
        smaServiceOrderTable.initFromAgreement();
        smaServiceOrderTable.updateProjAddress();
    }

    if (element.args() && element.args().dataset() == tableNum(ProjTable))
    {
        callerProjTable = element.args().record();

        smaServiceOrderTable.ProjId                      = callerProjTable.ProjId;
        smaServiceOrderTable.CustAccount                 = callerProjTable.CustAccount;
        postalAddress                                    = LogisticsPostalAddress::findByLocation(callerProjTable.DeliveryLocation);
        smaServiceOrderTable.ServiceAddressName          = postalAddress.displayLocationDescription();
        smaServiceOrderTable.ServicePostalAddress        = postalAddress.RecId;
    }
}

public void linkActive()
{
    switch (element.args().dataset())
    {
        // Is the form called from the activities form
        case tableNum(smmActivities) :
            smmActivityParentLink::updateQuery(this.query().dataSourceTable(tableNum(SMAServiceOrderTable)), element.args().record());
            break;
    }

    element.numberSeqFormHandler().formMethodDataSourceLinkActive();
    super();
}

public boolean validateDelete()
{
    boolean ret;
    boolean lineExist;

    ret = super();

    if (ret)
    {
        lineExist = SMAServiceOrderLine::existServiceOrderLine(smaServiceOrderTable.ServiceOrderId);

        if (lineExist && smaServiceOrderTable.Progress == SMAServiceOrderProgress::InProcess)
        {
            // Delete service order lines?
            if (Box::yesNo("@SYS91002", DialogButton::No) == DialogButton::No)
            {
                // Deletion has been canceled
                return checkFailed("@SYS19437");
            }
        }
    }

    return ret;
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    ret = element.numberSeqFormHandler().formMethodDataSourceValidateWrite(ret) && ret;

    return ret;
}

public void write()
{
    smmActivities   activities;
    SMAParameters   parameters = SMAParameters::find();

    try
    {
        // Should not be inside ttsbegin and ttscommit.
        smaServiceOrderTable.updateFromAdvPromptActivity(smaServiceOrderTable.orig());

        ttsbegin;
        super();
        element.numberSeqFormHandler().formMethodDataSourceWrite();
        ttscommit;
    }
    catch
    {
        if (smaServiceOrderTable.ActivityNumber && smaServiceOrderTable.orig().ActivityNumber != smaServiceOrderTable.ActivityNumber)
        {
            ttsbegin;

            smaServiceOrderTable.ActivityNumber = smaServiceOrderTable.orig().ActivityNumber;

            activities = smmActivities::find(smaServiceOrderTable.ActivityNumber, true);

            if (activities)
            {
                activities.delete(false);
            }

            ttscommit;
        }
    }

    element.setFieldAccessHeader();
}

