public void selectionChanged()
{
    smmActivities selectedActivities;
    str rangeActivityNumber;

    if (tabActivities.isActivePage())
    {
        selectedActivities = smmActivities_ds.getFirst(true);
        while (selectedActivities)
        {
            if (rangeActivityNumber)
            {
                rangeActivityNumber += ', ';
            }

            rangeActivityNumber += selectedActivities.ActivityNumber;
            selectedActivities = smmActivities_ds.getNext();
        }

        qbrActivityId.value(rangeActivityNumber);
        element.refreshLines();
    }

    super();
}

