display SMAMarkForFollowup markForFollowup(smmActivities _activity)
{
    #define.highPriority(3232)
    #define.normalPriority(3233)

    if (_activity.Closed)
    {
        if (_activity.TotalWork - _activity.ActualWork < 0)
        {
            return #highPriority;
        }
        else
        if (_activity.TotalWork - _activity.ActualWork > 0)
        {
            return #normalPriority;
        }
    }

    return 0;
}

