public void initValue()
{
    if (smaServiceOrderTable)
    {
        smaServiceOrderLine.initFromServiceOrder(smaServiceOrderTable.ServiceOrderId);

        smaServiceOrderLine.DateRangeFrom       = DateTimeUtil::date(DateTimeUtil::applyTimeZoneOffset(smaServiceOrderTable.ServiceDateTime,DateTimeUtil::getUserPreferredTimeZone()));
        smaServiceOrderLine.DateRangeTo         = DateTimeUtil::date(DateTimeUtil::applyTimeZoneOffset(smaServiceOrderTable.ServiceDateTime,DateTimeUtil::getUserPreferredTimeZone()));
        smaServiceOrderLine.DateExecution       = DateTimeUtil::date(DateTimeUtil::applyTimeZoneOffset(smaServiceOrderTable.ServiceDateTime,DateTimeUtil::getUserPreferredTimeZone()));

        smaServiceOrderLine.ServiceOrderStatus  = SMAServiceOrderStatus::Created;
        smaServiceOrderLine.Origin              = SMAServiceOrderOrigin::User;
        smaServiceOrderLine.Qty                 = 1;

        smaServiceOrderLine.TransactionType     = SMATransactionType::Hour;
        smaServiceOrderLine.ProjCategoryId      = ProjParameters::defaultProjCategoryBasedOnSMATransactionType(smaServiceOrderLine.TransactionType);

        smaServiceOrderLine.SMAServiceLineMap::setItemTaxGroupId();

        if (smaServiceOrderLine.ProjCategoryId != '')
        {
            smaServiceOrderLine.Description     = ProjCategory::find(smaServiceOrderLine.ProjCategoryId).Name;
        }

    }

    super();

    // Skip the Signoff field when creating a new line
    smaServiceOrderLine_ds.object(fieldNum(SMAServiceOrderLine, SignOff)).skip(true);

    smaServiceOrderLine.ActivityId = '';
}

