public boolean validateDelete()
{
    boolean         ret;

    // Check the service order lines can be deleted in the current stage
    if (!SMAStageTable::find(smaServiceOrderTable.StageId).StageCanDelete)
    {
        // The service order lines can't be deleted in the current stage
        return checkFailed("@SYS91567");
    }

    ret = super();

    return ret;
}

public void write()
{
    // <GIN>
    LogisticsLocationRecId  location_IN;
    // </GIN>

    if (smaServiceOrderLine.TransactionType == SMATransactionType::Item)
    {
        smaServiceOrderLine.InventDimId = InventDim::findOrCreate(inventDim).InventDimId;
    }
    else
    {
        smaServiceOrderLine.InventDimId = InventDim::inventDimIdBlank();
    }

    super();

    // the call to super() eventually updates the serviceOrderTable_ds
    // so we make sure we deal with the latest version
    smaServiceOrderTable_ds.reread();

    if (smaServiceOrderTable.Progress != SMAServiceOrderProgress::InProcess)
    {
        smaServiceOrderTable.updateProgress();
        // write() takes care of rereading the data (i.e. after the call the RecVersion is latest)
        smaServiceOrderTable.write();
    }

    // refresh UI
    smaServiceOrderTable_ds.refresh();

    if (tabActivities.isActivePage())
    {
        smmActivities_ds.executeQuery();
    }

    // <GIN>
    if (smaServiceOrderLineTaxExtension_IN_DS.isNeeded())
    {
        TaxModelDocLineExtensionController_IN::insertTaxExtensionRecord(smaServiceOrderLineTaxExtension_IN, smaServiceOrderLine);
    }
    // </GIN>
}

