public void modified()
{
    InventDim       tmpInventDim;
    WMSLocation     wmsLocation;

    super();

    tmpInventDim = InventDim::find(smaServiceOrderLine.InventDimId);
    if (tmpInventDim)
    {
        // Get default item location.
        wmsLocation = WMSLocation::defaultItemLocation(smaServiceOrderLine.ItemId, InventDirection::Issue, tmpInventDim);
        if (wmsLocation)
        {
            tmpInventDim.InventLocationId   = wmsLocation.InventLocationId;
            tmpInventDim.wmsLocationId      = wmsLocation.wmsLocationId;
        }
    }

    inventDim.data(tmpInventDim);
    element.updateDesign(InventDimFormDesignUpdate::FieldChange);
    // <GIN>
    if (TaxParameters::checkTaxParameters_IN())
    {
        smaServiceOrderLineTaxExtension_IN.initCompanyLocation();
    }
    // </GIN>
}

