public void jumpRef()
{
    Args    args;
    FormRun fr;
    Common  record;

    args = new Args(formStr(SMAServiceObjectRelation));
    record = SMAServiceObjectRelation::find(smaServiceOrderLine.ServiceObjectRelationId);

    args.caller(this);
    args.record(SMAServiceOrderTable::find(smaServiceOrderLine.ServiceOrderId));

    // Menu item info will be used by security framework, when setting access rights to form datasources
    args.menuItemType(MenuItemType::Display);
    args.menuItemName(menuitemDisplayStr(SMAServiceObjectRelation));

    fr = classfactory.formRunClass(args);
    fr.init();
    fr.run();
    fr.dataSource().findRecord(record);
    fr.detach();
}

