    else if (smaServiceOrderLine.Qty>0 && smaServiceOrderLineTaxExtension_IN.AssessableValueTransactionCurrency < 0)
    {
        smaServiceOrderLineTaxExtension_IN.AssessableValueTransactionCurrency = smaServiceOrderLine.Qty * smaServiceOrderLine.ProjSalesPrice;
        throw error("@GLS3800202");
    }
}

