    smaServiceOrderTable.updateProgress(SMAServiceOrderProgress::Canceled);
    smaServiceOrderTable.write();

    ttscommit;

    element.refreshLines();
    element.refreshHeader();
}

void clicked()
{
    super();

    ttsbegin;

    smaServiceOrderLine.revokeCancelLine();
    smaServiceOrderTable.updateProgress(SMAServiceOrderProgress::Canceled);
    smaServiceOrderTable.write();

    ttscommit;

    element.refreshLines();
}

