public Common lookupReference()
{
    Common ret;

    ret = hcmWorkerLookupCurrentCompany.lookupWorker(this);

    return ret;
}

public Common resolveReference()
{
    HcmWorker ret;

    ret = super();

    if (ret != null && !hcmWorkerLookupCurrentCompany.validateWorker(ret.RecId))
    {
        ret = null;
    }

    return ret;
}
